HttpRequest.BodyTemplate:
{
LET $duration := (ARRAY_INDEX(DATEDIFF_PARTS([Meeting Date], [Meeting Date End]), 4) * 60) + ARRAY_INDEX(DATEDIFF_PARTS([Meeting Date], [Meeting Date End]), 5);

RETURN
JSONENCODE(
  HASH(
  	"topic", [Meeting Title],
  	"type", 2,
  	"start_time", DATEFORMAT([Meeting Date], "yyyy-MM-ddTHH:mm:ssZ"),
  	"duration", $duration,
  	"password", "$" & LEFT(EBMS_GET_RANDOM_STRING(), 9),
  	"agenda", [Meeting Objective]
  )
)}

FieldEntry.Formula:
LET $columns := 
ARRAY(
  "Date Due",
  "Action",
  "Responsible - Multiline",
  "Responsible Text"
);

LET $results := 
ARRAY_SELECTMANY(
  [Action RecordIDs], $(i, item) 
	LET $data := 
	DB_LOOKUPALL(
	  $columns, 
	  "Action",
	  "[Responsible - Multiline] IS NOT EMPTY AND [RecordID] = " & $item, 
	  HASH("parent", ARRAY(EFormType, RecordID))
	);

	RETURN 
	$data
);

LET $results := 
ARRAY_SELECT(
	$results, $(i, item)
	INDEXES_TO_KEYS($item, $columns)
); 

LET $distinctAssignees := 
ARRAY_DISTINCT(ARRAY_SELECT(
  $results, $(i, item) 
  HASH_GET($item, "Responsible - Multiline")
));

LET $taskSets := 
ARRAY_SELECT(
	$distinctAssignees, $(i, assignee) 
	ARRAY_WHERE(
		$results, $(a, action) 
		HASH_GET($action, "Responsible - Multiline") = $assignee
	)
);

LET $emailAddresses := 
ARRAY_SELECT(
	$distinctAssignees, $(i, item) 
	EBMS_GET_ROLE_EMAIL_ADDRESSES(ROLE($item))
);

LET $emailsToSend := ARRAY_LENGTH($distinctAssignees);

RETURN 
HASH( 
	"Emails to send", $emailsToSend,
	"Email Recipients", $emailAddresses,
	"Action data", $taskSets
)

