MergeField.Formula:
LET $ratesOnlyWithScheduleItems :=
	ARRAY_TRANSPOSE(
		ARRAY(   
			EACH [Schedule Item - Rate Only], 
			EACH [Item Description - Rate Only],
			EACH [Unit - Rate Only],

			LET $rate := EACH [Rate - Rate Only];
			RETURN	ARRAY_SELECT($rate, $(i, item) NUMBERFORMAT($item, "C")),

			EACH [Claim Quantity - Rate Only],

			LET $claimAmount := EACH [Claim Amount - Rate Only];
			RETURN	ARRAY_SELECT($claimAmount, $(i, item) NUMBERFORMAT($item, "C")),

			LET $amountClaimed := EACH [Amount Claimed - Rate Only];
			RETURN	ARRAY_SELECT($amountClaimed, $(i, item) NUMBERFORMAT($item, "C"))
		  )
	);

LET $ratesOnlyWithScheduleItems :=
	ARRAY_SELECT(
	  	$ratesOnlyWithScheduleItems, $(i, item) 
	  	LET $sectionItem := ARRAY_INDEX($item, 1);
	  	LET $itemDescription := ARRAY_INDEX($item, 2);
	  	LET $values :=	
	  		ARRAY_SELECT(
		  		ARRAY_SKIP($item, 2),
		  			$(j, jtem)
				  	IF(
					  [Section Heading - Rate Only]#$i = TRUE,
					  NULL(),
					  $jtem
					)
				);
		  		RETURN 
			  	ARRAY_CONCAT(
				  	$sectionItem,
				  	$itemDescription,
		  			$values
			)
	 );

RETURN $ratesOnlyWithScheduleItems

LET $condition :=
	AND(
		[Schedule Of Rates RecordID] <> NULL(),
		OR(
			[Type] = "Rate Only",
			[Type] = "Mix"
		)
	);

RETURN $condition

LET $condition :=
	AND(
		[Schedule Of Rates RecordID] <> NULL(),
		[Lump Sum Type] = "Amount",
	  OR(
			[Type] = "Lump Sum",
			[Type] = "Mix"
		)
	);

RETURN $condition

LET $condition := 
		AND(
		[Schedule Of Rates RecordID] <> NULL(),
			OR(
			  [Lump Sum Type] = "Quantity",
			  [Lump Sum Type] = "Both"
			  ),

			OR(
				[Type] = "Lump Sum",
				[Type] = "Mix"
			)
		);

RETURN $condition

LET $scheduleLumpSumItems :=
	ARRAY_TRANSPOSE(
		ARRAY(   
			EACH [Schedule Item], 

			EACH [Item Description],

			LET $quantity := EACH [Quantity];
			RETURN	ARRAY_SELECT($quantity, $(i, item) NUMBERFORMAT($item, "N2"\)),

			EACH [Unit],


			LET $rate := EACH [Rate];
			RETURN	ARRAY_SELECT($rate, $(i, item) NUMBERFORMAT($item, "C")),


		  	LET $contractAmount := EACH [Contract Amount];
			RETURN	ARRAY_SELECT($contractAmount, $(i, item) NUMBERFORMAT($item, "C")),

			LET $remainingQuantity := EACH [Remaining Quantity];
			RETURN	ARRAY_SELECT(
						$remainingQuantity, $(i, item) 
						IF(
							[Qty or Amt]#$i = "Amount",
							"N/A",
							NUMBERFORMAT($item, "N2"\)
						)
			 	),

		  	LET $remainingAmount := EACH [Remaining Amount];
			RETURN	ARRAY_SELECT($remainingAmount, $(i, item) NUMBERFORMAT($item, "C")),

			LET $percentMovement := EACH [% Movement];
			RETURN	ARRAY_SELECT($percentMovement, $(i, item) NUMBERFORMAT($item, "N2"\) & "%"),

			EACH [Movement text],

			LET $claimQuantity := EACH [Claim Quantity];
			RETURN	ARRAY_SELECT(
			  		$claimQuantity, $(i, item) 
			  		IF(
					  	[Qty or Amt]#$i = "Amount",
					  	"N/A",
					  	NUMBERFORMAT($item, "N2"\)
			  		)
				),


		  	LET $claimAmount := EACH [Claim Amount];
			RETURN	ARRAY_SELECT($claimAmount, $(i, item) NUMBERFORMAT($item, "C")),

			LET $amountClaimed := EACH [Amount Claimed];
			RETURN	ARRAY_SELECT($amountClaimed, $(i, item) NUMBERFORMAT($item, "C"))


		)
	);

LET $scheduleLumpSumItems :=
	ARRAY_SELECT(
	  	$scheduleLumpSumItems, $(i, item) 
	  	LET $sectionItem := ARRAY_INDEX($item, 1);
	  	LET $itemDescription := ARRAY_INDEX($item, 2);
	  	LET $values :=	
	  		ARRAY_SELECT(
		  		ARRAY_SKIP($item, 2),
		  			$(j, jtem)
				  	IF(
					  [Section Heading]#$i = TRUE,
					  NULL(),
					  $jtem
					)
				);
		  		RETURN 
			  	ARRAY_CONCAT(
				  	$sectionItem,
				  	$itemDescription,
		  			$values
			)
	 );

RETURN  $scheduleLumpSumItems

