FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $data:= ARRAY_TRANSPOSE( DB_LOOKUPALL( ARRAY( "Section Heading", "Section Heading ID", "ID", "Description of Work", "Item Contracted Amount", "Estimated Quantity", "Unit", "Rate $", "Tax Percent", "Qty or Amt", "% Complete", "Item Remaining Amount", "Item Remaining Quantity", "Section Heading - Rate Only", "ID - Rate Only", "Description of Work - Rate Only", "Rate $ - Rate Only", "Unit - Rate Only", "SOR Type" ), "Schedule of Rates", "", HASH("parent", ARRAY("Contract", [Contract RecordID])) ) ); LET $id:= ARRAY_FIRST( ARRAY_FIRST( DB_REPORT( "COUNT([RecordID])", "Payment Certificate", "[RecordID] <> " & [RecordID] & " AND [LineNumber] FAST_EQUAL_TO 1", HASH("parent", ARRAY("Contract", [Contract RecordID])) ) ) ) + 1;
LET $data:= INDEXES_TO_KEYS( $data, ARRAY( "Section Heading", "Section Heading ID", "Schedule Item", "Item Description", "Contract Amount", "Quantity", "Unit", "Rate", "Tax Percentage", "Qty or Amt", "% Comp", "Remaining Amount", "Remaining Quantity", "Section Heading - Rate Only", "Schedule Item - Rate Only", "Item Description - Rate Only", "Rate - Rate Only", "Unit - Rate Only", "Type"
)
);
RETURN HASH_MERGE( $data, HASH( "Invoice Number", ARRAY($id), "ClaimID", ARRAY($id) )
)

Binding.Expression:
LET $tick := 
"<span><svg viewBox=""0 0 512 512"" class=""svg-inline--fa fa-check fa-w-16"" data-icon=""check"" data-prefix=""fas"" aria-hidden=""true"" xmlns=""http://www.w3.org/2000/svg"" style=""fill: #0d851e; font-size: 13px;""><path d=""M173.898 439.404l-166.4-166.4c-9.997-9.997-9.997-26.206 0-36.204l36.203-36.204c9.997-9.998 26.207-9.998 36.204 0L192 312.69 432.095 72.596c9.997-9.997 26.207-9.997 36.204 0l36.203 36.204c9.997 9.997 9.997 26.206 0 36.204l-294.4 294.401c-9.998 9.997-26.207 9.997-36.204-.001z"" fill=""""></path></svg></span>";

LET $pool := 
ARRAY(
	ARRAY(
		"Schedule of Rates figures will be updated", 
		[Schedule Of Rates RecordID] <> NULL()
	),

	ARRAY(
		"Generation of 'Payment Certificate' document will become available", 
		TRUE
	)
);

LET $approvalEvents := 
  ARRAY_SELECT(
	  ARRAY_WHERE(
		  $pool, $(i, item) 
		  ARRAY_INDEX($item, 2) == TRUE
	  ), $(i, item)  ARRAY_INDEX($item, 1) 
  );

RETURN 
  "<div style='padding-top: 4px; height: 80px; overflow: auto;'><p>On approval the following events will happen:</p>" & 
  "<ol>" & ARRAY_JOIN(
  ARRAY_SELECT(
	$approvalEvents, $(i,item) "<li>" & HTMLENCODE($item) & //IF(EVALUATE("[" & $item & "]") <> NULL, "<span style='padding-left: 5px;'>" & $tick & "</span>") & 
	"</li>"
  )) & 
  "</ol>"

TriggerCustomEvent.EventArgumentsFormula:
LET $targetType := "Payment Certificate";
LET $mostRecentSubmission := DB_LOOKUP( ARRAY("RecordID", "Round"), "Submission", "[Type] = " & DB_ENCODECONSTANT($targetType), HASH("sort", "[Round] DESC", "ancestor", ARRAY(EFormType, RecordID)) );
RETURN HASH( "Submission Type", $targetType, "Previous Submission Round", COALESCE(ARRAY_LAST($mostRecentSubmission), 0), "Previous Submission RecordID", ARRAY_FIRST($mostRecentSubmission), "Parent Record", ARRAY([EFormType], [RecordID]), "Review Record", ARRAY([EFormType], [RecordID]), "Submitter Notes", NULL )

MailMerge.ResultFileNameTemplate:
{LET $name:= [Contract Number] & "-"  & " " & [Contract Title] & " #"  & [Invoice Number] & " " & HASH_GET([Mail Merge Options - Document Selection], "FileName");

LET $cleanName:= REGEX_REPLACE($name,  "[\*\\\:\<\>\?\/\|\""]", $(i, item) "");

LET $cleanName := LEFT($cleanName, FIND(".", $cleanName)-1);

RETURN DATEFORMAT(NOW(), "yyyy-MMM-dd") & " - " & $cleanName & ".docx"}

{LET $name:= [Contract Number] & "-"  & " " & [Contract Title] & " #"  & [Invoice Number] & " " & [Mail Merge Purpose];
LET $cleanName:= REGEX_REPLACE($name,  "[\*\\\:\<\>\?\/\|\""]", $(i, item) "");
RETURN DATEFORMAT(NOW(), "yyyy-MMM-dd") & " - " & $cleanName & ".docx"}

Binding.Expression:
NOT(
  AND(
    [Mail Merge Options - Document Selection] <> NULL, 
    HASH_GET([Mail Merge Options - Document Selection], "FileName") <> [Mail Merge Purpose]  
  )
)

LET $results := HASH_GET([Mail Merge Options], "Custom Templates");

LET $hashed :=  
ARRAY_SELECT(
  $results, $(i, item) 
  HASH(
	"text", HASH_GET($item, "FileName"),
	"value", $item
  )
);

RETURN $hashed

