FieldEntry.Formula:
LET $set :=
ARRAY(
	ARRAY(
		HASH_GET($eventargs, "Parent Record"),
		HASH_MERGE(
			HASH("Payment Certificate Approved", TRUE),
			HASH("Status", "Approved")
		)
	),

	ARRAY(
		ARRAY("Contract", [Contract RecordID]),
		HASH("Temp", NULL())
	),

	IF( 
		[Schedule Of Rates RecordID] <> NULL(), 
		ARRAY(
			ARRAY("Schedule of Rates", [Schedule Of Rates RecordID]),
			HASH(
				"% Complete", EACH [% Comp]
			)
		), 
		NULL()
	)
);

RETURN 
ARRAY_WHERE($set, $(i, item) $item <> NULL())

