LET $tdata:= INDEXES_TO_KEYS( $data, ARRAY( "Section Heading", "Section Heading ID", "Schedule Item", "Item Description", "Contract Amount", "Quantity", "Unit", "Rate", "Tax Percentage", "Qty or Amt", "% Comp", "Remaining Amount", "Remaining Quantity", /*"Section Heading - Rate Only", "Schedule Item - Rate Only", "Item Description - Rate Only", "Rate - Rate Only", "Unit - Rate Only",*/ "Type", "Lump Sum Type"
)
);
RETURN $tdata

