Navigate.UrlTemplate:
{
LET $url := HASH_GET(ARRAY_INDEX([Document Data], [Document Data - Display Index]), "URL");
RETURN 
IF(
  $url <> NULL,
    $url,
  "files/eformrecords/Respondent/" & 
  HASH_GET(ARRAY_INDEX([Document Data], [Document Data - Display Index]), "Respondent Source RecordID") & 
  "/Response/" & 
  URLENCODE(HASH_GET(ARRAY_INDEX([Document Data], [Document Data - Display Index]), "File Name"))
)
}

Binding.Expression:
"<style>
.coreDocumentHidden {
  background; #d8d8d8; 
  color: #404040;
}
.coreDocument:hover {
  color: #00557A;
}
.coreDocument {" &
  IF([Enable Removing Documents] = TRUE, "width: 534px;") & "
  overflow: hidden;
  text-overflow: ellipsis;
  line-height: 30px;
  white-space: nowrap;
  background: white;
  border: 1px solid #e0e0e0;
  font-size: 10pt;
  padding-left: 4px;
  cursor: pointer;
}
</style>"

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    [Teams], 
	[Team UUIDs] 
  )
); 

RETURN 
ARRAY_SELECT(
  $data, $(i, item) 
  HASH("value", ARRAY_INDEX($item, 2), "text", ARRAY_INDEX($item, 1))
)

FieldEntry.Formula:
ARRAY_SELECT(
  [Document Data], $(i, item)
  LET $currentAssigned := HASH_GET($item, "Assigned Teams");

  LET $updatedAssigned := 
  ARRAY_DISTINCT(
	ARRAY_WHERE(
	  $currentAssigned, $(i, item) 
	  AND(
		$item <> NULL, 
		ARRAY_INDEXOF([Document Allocation - Teams], $item) = NULL
	  )
    )
  ); 

  LET $responseId := HASH_GET($item, "Response ID");
  LET $responseName := HASH_GET($item, "Response Name");

  LET $respondentLine := ARRAY_INDEXOF(EACH [Respondent], $responseName);
  LET $excluded := [Respondent Status]#$respondentLine = "Excluded";   
  RETURN 
  HASH_MERGE(
	$item, 
	HASH(
	  "Assigned Teams", 
	  IF($excluded = TRUE, NULL, $updatedAssigned)
	)
  )
)

BulkCreateRecords.FieldValuesFormula:
LET $hashed := 
ARRAY_TO_HASH_GROUPED(
  [Document Data], 
  $(i, item) HASH_GET($item, "Respondent Source RecordID"),
  $(i, item) 
  ARRAY(
	HASH_GET($item, "Respondent Source Line"), 
	HASH_GET($item, "Assigned Teams")
  )
); 

LET $map :=  
INDEXES_TO_KEYS( 
  ARRAY_SELECT(
    HASH_KEYS($hashed), $(i, item) 
    LET $transposed := ARRAY_TRANSPOSE(HASH_GET($hashed, $item));
    RETURN INDEXES_TO_KEYS(ARRAY_LAST($transposed), ARRAY_FIRST($transposed))
  ), 
  HASH_KEYS($hashed)
);

RETURN
ARRAY_SELECT(
  HASH_KEYS($hashed), $(d, recordID)
  LET $data := HASH_GET($hashed, $recordID);
  LET $maxLine := GREATEST(HASH_KEYS($data)); 

  LET $assignEachLine := 
  ARRAY_SELECT( 
	RANGE(1, $maxLine), $(i, line) 
	HASH_GET($data, $line)
  );
  RETURN  
  HASH(
      "content", 
      HASH(
        "Assigned Teams",
          $assignEachLine
      ),

      "match", 
        HASH("RecordID", $recordID)  
    )
)

FieldEntry.Formula:
ARRAY_SELECT(
  [Document Data], $(i, item)
  LET $currentAssigned := HASH_GET($item, "Assigned Teams"); 
  LET $updatedAssigned := 
  ARRAY_DISTINCT(
	ARRAY_WHERE(
	  ARRAY_CONCAT($currentAssigned, [Document Allocation - Teams]), 
	  $(i, item) $item <> NULL
    )
  ); 

  LET $responseId := HASH_GET($item, "Response ID");
  LET $responseName := HASH_GET($item, "Response Name");

  LET $respondentLine := ARRAY_INDEXOF(EACH [Respondent], $responseName);
  LET $excluded := [Respondent Status]#$respondentLine = "Excluded";   
  RETURN 
  HASH_MERGE(
	$item, 
	HASH(
	  "Assigned Teams", 
	  IF($excluded = TRUE, NULL, $updatedAssigned)
	)
  )
)

