FieldEntry.Formula:
IF( 
  [Document Allocation - Team - Filter] <> NULL, 
  ARRAY_WHERE(
    [Document Data], $(i, item) 
    (
      LET $assignedTeams := HASH_GET($item, "Assigned Teams"); 

      RETURN
      IF( 
        [Document Allocation - Team - Filter] = NULL, 
          1=1,

    	  [Document Allocation - Team - Filter] = "<Unallocated>", 
    	  OR(
            $assignedTeams = ARRAY(), 
            $assignedTeams = NULL
    	  ), 

    	  ARRAY_INDEXOF(
    	    $assignedTeams, 
    	    [Document Allocation - Team - Filter]
    	  ) > 0
      )
    )
  )
)

