FieldEntry.Formula:
LET $thisDocReference := 
ARRAY(
  HASH_GET(ARRAY_INDEX([Document Data], [Document Data - Display Index]), "Respondent Source RecordID"),
  HASH_GET(ARRAY_INDEX([Document Data], [Document Data - Display Index]), "Respondent Source Line")
); 

RETURN 
ARRAY_SELECT(
  [Document Data], $(i, item) 
  IF(
    AND(
	  ARRAY_FIRST($thisDocReference) = HASH_GET($item, "Respondent Source RecordID"),
      ARRAY_LAST($thisDocReference) = HASH_GET($item, "Respondent Source Line")
	), 
	HASH_MERGE(
      $item, 
	  HASH("Assigned Teams", [Document Data - Display Teams])
	), 
	$item
  )
)

