Binding.Expression:
LET $line := [Document Data - Display Index]; 
LET $respondentName := HASH_GET(ARRAY_INDEX([Document Data], $line), "Respondent Name");
LET $responseID := HASH_GET(ARRAY_INDEX([Document Data], $line), "Response ID");
LET $fileName := HASH_GET(ARRAY_INDEX([Document Data], $line), "File Name");

LET $responseIDString := 
IF( 
  NOT(ISBLANK($responseID)), 
    " (" & 
      IF(
  	    EBMS_CONTAINS([Respondent UUID]#1, "VPR"), 
  	    "VPR"
      ) & $responseID & 
    ")"
);

LET $nameLengthLimit := 50; 

LET $respondentName := 
IF( 
  LEN($respondentName) > $nameLengthLimit, 
    LEFT($respondentName, $nameLengthLimit) & "...", 
  $respondentName
); 

LET $displayText := 
IF( 
  [Selected Response Line] = NULL, 
    $respondentName & $responseIDString & " - "
) & $fileName;

LET $hidden := 
IF( 
  HASH_GET(ARRAY_INDEX([Document Data], [Document Data - Display Index]), "Document Status") = "Hidden", 
  TRUE
); 

RETURN 

"<div data-clickid='download' class='coreDocument" & IF($hidden = TRUE, " coreDocumentHidden") & "'>" & HTMLENCODE($displayText) & "</div>"

FieldEntry.Formula:
LET $attachments := EBMS_GET_ATTACHMENTS([EFormType], [RecordID], "Temp");

LET $attachmentData := 
(
  LET $columns := 
    ARRAY(
    "Filename", 
    "MimeType", 
    "File size", 
    "Provided via Sourcing"
  );

  LET $transformed :=  
  ARRAY_SELECT(
	  $attachments, $(i, item) 
	  (
		LET $fileName := HASH_GET($item, "FileName"); 
		LET $mimeType := HASH_GET($item, "MimeType"); 
		LET $fileSize := HASH_GET($item, "Length"); 

		LET $providedViaSourcing := FALSE;

		RETURN ARRAY($fileName, $mimeType, $fileSize, $providedViaSourcing)
	  )
  );

  LET $filtered := 
  ARRAY_WHERE(
	$transformed, $(i, item) 
	1=1
	//AND(
	//  RIGHT(ARRAY_INDEX($item, 1), 4) <> ".zip",
	//  LEFT(ARRAY_INDEX($item, 1), 8) <> "Response"
	//)
  ); 

  RETURN 
  INDEXES_TO_KEYS(
	ARRAY_TRANSPOSE($filtered), 
	$columns
  )
);

RETURN $attachmentData

LET $action := ARRAY( HASH( "Action", "Added", "DateTime", NOW(), "Files", HASH_GET([Temp], "Filename") ) );
RETURN IF( [Document Change Log] = NULL, $action,
ARRAY_CONCAT( [Document Change Log], $action ) )

Binding.Expression:
AND(
  [Adding Documents] = TRUE
)

LET $dataSet := 
COALESCE(
  [Document Data - Filtered Matches], 
  [Document Data]
); 

LET $totalDocuments := ARRAY_LENGTH($dataSet);
LET $from := ([Document Data - Display Page] - 1) * 10 + 1;
LET $to := $from + ARRAY_LENGTH(EACH [Document Data - Display Index]) - 1; 

RETURN 
IF( 
  [Document Data - Display Index] <> NULL, 
  $from & " - " & $to & " of " & $totalDocuments & " documents"
)

