FieldEntry.Formula:
LET $pageLength := 10; 

LET $dataSet := 
COALESCE(
  [Document Data - Filtered Matches],
  [Document Data]
); 

LET $data :=  
ARRAY_TAKE(
  ARRAY_SKIP(
    $dataSet,
	([Document Data - Display Page] - 1) * $pageLength
  ), 
  $pageLength
);

RETURN
//outputs the id of the item as it is in the document data array
ARRAY_SELECT(
  $data, $(i, item) 
  ARRAY_INDEXOF([Document Data], $item)
)
