Column.DefaultValueFormula:
LET $columns := ARRAY( "Owns Integrated Module - Procurement Planning", "Owns Integrated Module - Evaluations", "Owns Integrated Module - Contracts", "Owns Integrated Module - Go To Market", "Owns Integrated Module - Purchase Orders" );
LET $data := DB_LOOKUP( $columns, "Global Settings", "[RecordID] = 1" );
RETURN INDEXES_TO_KEYS($data, $columns)

Column.Formula:
LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName:= HASH(
  	"Insurance",	"Compliance"
  );

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF( $targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title:= 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID)
( EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

	  LET $parent := 
	  IF( 
		[RecordID] > 0, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				  )

			  )
	  )); 

	  LET $gParent := 
	  IF( 
		 $parent <> NULL, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
			  )
		  )
	  )); 

	  LET $hash :=  
	  HASH_MERGE(
		  IF( $parent <> NULL, HASH("parent", 	$parent)),
		  IF( $gParent <> NULL, HASH("gparent", 	$gParent))
	  );

	  LET $parentStructure := 	//2nd instance
	  IF( 
		  ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
	  );

	  LET $parentData:= 
	  ARRAY_WHERE(
		ARRAY(
			$gParent, 
			$parent, 
			ARRAY_CONCAT($current, $currentDes, $currentAccess),
		), $(i,item) $item <> NULL
	   );


	  LET $parentChain:=
		ARRAY_SELECT(
			  $parentData, $(i, item)
			  ARRAY_TAKE($item, 2)
	   );


	  LET $parentLinks :=
	  ARRAY_SELECT(
		  $parentData, $(i, item) 
			  LET $eFormType 		:= ARRAY_FIRST($item);
			  LET $recordID 		:= ARRAY_INDEX($item, 2);
			  LET $descriptiveLabel := ARRAY_INDEX($item, 3);
			  LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
			  LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
			  LET $showEFormType := $eFormType <> "Vendor";

			  LET $labelColour := 
				  IF(
				   AND($eFormType = [EFormType], $recordID = [RecordID]),
					  "background: #ffffff; color: #5D5D5D;",
					 $hasReadAccess = TRUE,
					  "background: #ffffff; color: #4D87B7;",
					  "background: #ffffff; color: #5D5D5D;"

				  );



			  LET $labeltext:= 
					  IF( $showEFormType == TRUE, " " & COALESCE(HASH_GET($altFormName, $eFormType), $eFormType) ) &
					  IF(NOT(ISBLANK($descriptiveLabel)), " " & $descriptiveLabel);

			  LET $label :=  
				  "<span style=""margin-left: 5px;" & $labelColour & """>" &
				  HTMLENCODE($labeltext) & 
				  "</span>";

			  RETURN 
			  IF( 
				  $hasReadAccess == TRUE, 
				  "<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
				  $label 
			  )
	  );

	   LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 18px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";

	  LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

	  RETURN 
	  HASH(
		  "Parent Structure",	$parentStructure,
		  "Parent Data", 		$parentData,
		  "Parent Chain",		$parentChain,
		  "Breadcrumb",		$breadcrumb
		)
	 )
)

LET $columns := 
ARRAY(
  "Heading", 
  "Process Enabled", 
  "Stage", 
  "Process Display Name"
); 

//---- Steps

LET $filtered := 
ARRAY_WHERE(
  ARRAY_TRANSPOSE(
    ARRAY(
      EACH [Heading],
      EACH [Process Enabled],
      EACH [Stage],
      EACH [Process Display Name]
    )
  ), $(i ,item) 
  AND(
    ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Process Enabled")) = TRUE,
	ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Heading")) <> TRUE
  )
);

LET $steps := ARRAY_SELECT($filtered, $(i, item) ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Process Display Name")));

LET $evaluation := ARRAY_INDEXOF($steps, "Evaluation Planning") > 0; 

LET $text := 
ARRAY(
  "Stages: " & ARRAY_JOIN($stages, ", "),
  "Approvals: " & ARRAY_JOIN($approvals, ", ")
);

LET $sentenceHash := 
HASH(
  "Budget", 				"planning a budget", 
  "Timeline", 				"building a timeline",
  "Evaluation Planning", 	"identifying risks", 
  "Risks", 					"planning an evaluation process", 
  "Assessment(s)", 			"conducting an assessment to determine a procurement approach", 
  "Approval", 				"obtaining procurement plan approval", 
  "Go to Market", 			"advertising in the market", 
);

LET $combinedText :=  
ARRAY_WHERE(ARRAY_CONCAT(
  ARRAY_SELECT(
    $steps, $(i, item) 
    HASH_GET($sentenceHash, $item)
  ), 
  ARRAY(
    IF($evaluation = TRUE, "conducting an evaluation"),
	"finalising an award"
  )
), $(i, item) $item <> NULL);

LET $text := ARRAY_JOIN($combinedText, ", "); 

LET $result := "Requires (at minimum): " & UPPER(LEFT($text, 1)) & RIGHT($text, LEN($text) - 1) & "."
;

RETURN 
IF( 
  [Override Statement] = TRUE, 
  [Summary Statement], 
  $result
)

LET $set := ARRAY_TRANSPOSE(ARRAY( EACH [Setup Process Enabled], EACH [Setup Process], EACH [Setup Process Display Name] ));
LET $filtered := ARRAY_WHERE( $set, $(i, item) ARRAY_INDEX($item, 1) = TRUE );
RETURN ARRAY_TO_HASH( $filtered, $(i, item) ARRAY_INDEX($item, 2), $(i, item) HASH("Display Name", ARRAY_INDEX($item, 3)) )

FieldEntry.Formula:
LET $data := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Stage], 
		EACH [Process Enabled], 
		EACH [Requires Met]
	)
);

RETURN
ARRAY_SELECT(
	$data, $(i, item) 

	LET $stage := ARRAY_INDEX($item, 1); 
	LET $processenabled := ARRAY_INDEX($item, 2); 
	LET $requiresMet := ARRAY_INDEX($item, 3); 

	RETURN
	IF( 
		$requiresMet <> TRUE,
		FALSE,

		AND(
			ARRAY_INDEX($item, 1) = [Stage]#[LineNumber],
			[Process Enabled]#[LineNumber] == FALSE
		),
		FALSE, 

		AND(
			ARRAY_INDEX($item, 1) = [Stage]#[LineNumber],
			[Process Enabled]#[LineNumber] == TRUE
		),
		TRUE, 

		[Process Enabled]#$i
	)
)

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Process Enabled],
    EACH [Requires Met]
  )
);

RETURN 
ARRAY_SELECT(
  $data, $(i,item) 
  LET $enabled := ARRAY_INDEX($item, 1); 
  LET $requiresMet := ARRAY_INDEX($item, 2);  

  RETURN 
  IF( 
	$requiresMet <> TRUE,

	FALSE,  
	$enabled

	)
)

LET $set := ARRAY_TRANSPOSE( ARRAY( EACH [Stage], EACH [Process Enabled], EACH [Heading] ) );
LET $allFalse:= ARRAY_LENGTH( ARRAY_WHERE( $set, $(i, item) AND( ARRAY_INDEX($item, 1) = [Stage]#[LineNumber], ARRAY_INDEX($item, 2) == TRUE, ARRAY_INDEX($item, 3) <> TRUE ) ) ) <= 0;
RETURN ARRAY_SELECT( $set, $(i, item) IF( AND( ARRAY_INDEX($item, 3) <> TRUE, ARRAY_INDEX($item, 1) = NULL() ), NULL(),
AND( ARRAY_INDEX($item, 1) = [Stage]#[LineNumber], [Process Enabled]#[LineNumber] == TRUE, [Heading]#$i == TRUE ), TRUE,
AND( ARRAY_INDEX($item, 1) = [Stage]#[LineNumber], $allFalse == TRUE, [Heading]#$i == TRUE ), FALSE,
[Process Enabled]#$i ) )

LET $thisProcess := [Process];
LET $thisProcessEnabled := [Process Enabled];

LET $set := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Process], 
		EACH [Process Enabled],
		EACH [Requires],
	  EACH [Heading]
	)
);

RETURN 
ARRAY_SELECT(
	$set, $(i,item) 
	LET $process := ARRAY_INDEX($item, 1);
	LET $enabled := ARRAY_INDEX($item, 2);
	LET $requires := ARRAY_INDEX($item, 3);
	LET $heading := ARRAY_INDEX($item, 4);

	LET $processRequiresthisProcess := ARRAY_INDEXOF(
		$requires, 
		$thisProcess
	) > 0;

	RETURN 
	IF( 
	  	AND($heading <> TRUE, $process = NULL()), NULL(),

		AND(
		  $processRequiresthisProcess == TRUE, 
		  $thisProcessEnabled <> TRUE, 
	    ), 
	    FALSE, 

		$enabled
	)
)

