FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $columns:=
ARRAY(
  "Setup Process Enabled",
  "Setup Process",
  "Setup Process Display Name",
  "Setup Process Locked"
);


LET $buildArray:= $(columnName, line, direction)(
	LET $array	:= CURRENT_EACHVALUE($columnName);
	RETURN
		IF( $direction = "up",
			ARRAY_CONCAT(
			  //	$direction,
				ARRAY_TAKE($array, $line-2), //$before,
				CURRENT_VALUE($columnName, $line), 
			  	CURRENT_VALUE($columnName, $line-1),
				ARRAY_SKIP($array, $line) //$after
			),
			$direction = "down",
			ARRAY_CONCAT(
			  //	$direction,
				ARRAY_TAKE($array, $line-1), //$before,
				CURRENT_VALUE($columnName, $line+1), 
			  	CURRENT_VALUE($columnName, $line),
				ARRAY_SKIP($array, $line+1) //$after
			),
			$array
		  )
);
RETURN 
ARRAY_TO_HASH(
  $columns, 
  $(i, columnName) $columnName, //key is column name
  $(i, columnName) CALL( $buildArray, $columnName, HASH_GET($eventargs, "line"), HASH_GET($eventargs, "direction"))
  )

LET $defaultMatrix := 
//1. Enabled
//2. Stage
//3. Process Name - Note even though there are multiple stages, the Process Name must be completely unique
//4. Display value - in case a process name doesn't line up with client terminology we display this as a way to not have to change the actual data - if NULL then uses the process name - these display values only apply to this form and isn't reflected anywhere else
//5. Is heading row
//6. Is mandatory in all cases
//7. Array of Processes Name (3.) that are dependent on this process being select (driven through read only rules)

ARRAY(
	//ARRAY(TRUE, "Initiate", 	NULL(), 													NULL(), 			TRUE,  	TRUE, 	NULL()),
