    ARRAY(TRUE, "Plan", 		"Custom Reporting Fields",			 						"Reporting Fields",	FALSE, 	FALSE, 	NULL()),
	//ARRAY(TRUE, "Plan", 		"Procurement Officer - Conflict of Interest Declaration", 	"Conflict of Interest",		FALSE, 	FALSE, 	ARRAY("Roles")),
	//ARRAY(TRUE, "Plan", 		"Evaluation Criteria", 										NULL(),				FALSE, 	FALSE, 	NULL()),
	ARRAY(TRUE, "Plan", 		"Plan - Checklist", 										"Checklist",		FALSE, 	FALSE, 	NULL()),
	ARRAY(TRUE, "Plan", 		"Procurement Plan Approval",								"Approval",			FALSE, 	FALSE, 	NULL()),
