	//ARRAY(TRUE, "Advertise", 	"Addenda", 													"Log Addenda",		FALSE, 	FALSE, 	NULL()),
	//ARRAY(TRUE, "Advertise", 	"Questions", 												"Log Questions",	FALSE, 	FALSE, 	NULL()),
	ARRAY(TRUE, "Advertise", 	"Advertise - Checklist", 									"Checklist",		FALSE, 	FALSE, 	NULL()),

    ARRAY(TRUE, "Award", 		NULL(), 													NULL(),				TRUE,  	NULL(), NULL()),
	//ARRAY(TRUE, "Award", 		"Final Negotiations", 										NULL(),				FALSE, 	FALSE, 	NULL()), 		
	//ARRAY(TRUE, "Award", 		"Create Award Records", 									NULL(),				FALSE, 	FALSE, 	NULL()),
	//ARRAY(TRUE, "Award", 		"Letter Generation", 										NULL(),				FALSE, 	FALSE, 	NULL()), 		
	//ARRAY(TRUE, "Award", 		"Respondent Notifications", 								NULL(),				FALSE, 	FALSE, 	NULL()), 		
	//ARRAY(TRUE, "Award", 		"Lessons Learnt", 											NULL(),				FALSE, 	FALSE, 	NULL()), 		
