LET $defaultMatrix := 
//populate display values where NULL
ARRAY_SELECT(
  $defaultMatrix, $(i, item) 
  LET $enabled 		:= ARRAY_INDEX($item, 1);
  LET $stageName 	:= ARRAY_INDEX($item, 2);
  LET $processName 	:= ARRAY_INDEX($item, 3);
  LET $displayName 	:= COALESCE(ARRAY_INDEX($item, 4), $processName, $stageName);
  LET $isHeading 	:= ARRAY_INDEX($item, 5);
  LET $isRequired 	:= ARRAY_INDEX($item, 6);
  LET $requires 	:= ARRAY_INDEX($item, 7);

  RETURN ARRAY($enabled, $stageName, $processName, $isHeading, $isRequired, $requires, $displayName)
); 


LET $getSavedValue := $(stage, process, indexToCollect)(
	LET $match := 
	ARRAY_FIRST(
		ARRAY_WHERE(
			$previousSettings, $(i, item)
			AND(
				ARRAY_INDEX($item, 2) = $stage,
				ARRAY_INDEX($item, 3) = $process
			)
		)
	);

	RETURN 
	ARRAY_INDEX($match, $indexToCollect)
); 

RETURN 
ARRAY_SELECT(
	$defaultMatrix, $(i, item)
	LET $stage 			:= ARRAY_INDEX($item, 2);
	LET $process 		:= ARRAY_INDEX($item, 3);
	LET $displayName 	:= ARRAY_INDEX($item, 7);

	LET $enabled 	:= COALESCE(CALL($getSavedValue, $stage, $process, 1), ARRAY_INDEX($item, 1));
	LET $heading 	:= /*COALESCE(CALL($getSavedValue, $stage, $process, 4),*/ ARRAY_INDEX($item, 4)/*)*/;
	LET $required 	:= /*COALESCE(CALL($getSavedValue, $stage, $process, 5),*/ ARRAY_INDEX($item, 5)/*)*/;
	LET $requires 	:= /*COALESCE(CALL($getSavedValue, $stage, $process, 6),*/ ARRAY_INDEX($item, 6)/*)*/;

	RETURN 
	ARRAY($enabled, $stage, $process, $heading, $required, $requires, $displayName)
)

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $defaultMatrix := 
//1. Enabled
//2. Process Name - Note even though there are multiple stages, the Process Name must be completely unique
//3. Display value - in case a process name doesn't line up with client terminology we display this as a way to not have to change the actual data - if NULL then uses the process name - these display values only apply to this form and isn't reflected anywhere else
//4. Is locked

ARRAY(
	ARRAY(TRUE,   		"High Level Details",		"Details",		TRUE),
	ARRAY(TRUE,   		"Value", 					NULL,			TRUE),
	ARRAY(TRUE,   		"Procurement Process",		NULL,			TRUE),
	ARRAY(TRUE,   		"Roles", 					NULL,			TRUE),
	ARRAY(IF([Setup Approval Required] = TRUE, TRUE, FALSE),  		"Approval",					"Approval",		FALSE),
	ARRAY(FALSE,  		"Setup - Checklist",		"Checklist",	FALSE)
);

LET $defaultMatrix := 
//populate display values where NULL
ARRAY_SELECT(
  $defaultMatrix, $(i, item) 
  LET $enabled 		:= ARRAY_INDEX($item, 1);
  LET $processName 	:= ARRAY_INDEX($item, 2);
  LET $displayName 	:= COALESCE(ARRAY_INDEX($item, 3), $processName);
  LET $isLocked 	:= ARRAY_INDEX($item, 4);

  RETURN ARRAY($enabled, $processName, $displayName, $isLocked)
); 

LET $defaultHash:= 
ARRAY_TO_HASH_GROUPED(
  	$defaultMatrix, 
  	$(i,item) ARRAY_INDEX($item, 2),
  	$(i,item) $item
  );

LET $columns:=
ARRAY(
  "Setup Process Enabled",
  "Setup Process",
  "Setup Process Display Name",
  "Setup Process Locked"
  );

LET $previousSettings:= 
ARRAY_TRANSPOSE(
  ARRAY_SELECT($columns,
	$(c, columnName) CURRENT_EACHVALUE($columnName)
  	)
  );

LET $previousSettingsHash :=
	ARRAY_TO_HASH_GROUPED(
		$previousSettings, 
		$(i,item) ARRAY_INDEX($item, 2),
		$(i,item) $item
	 );

//function to update processes for a stage
LET $updateProcess := $(stage)(

  	LET $defaultSet:= HASH_GET($defaultHash, $stage);
  	LET $previousSet:= HASH_GET($previousSettingsHash, $stage);

  	LET $default:=  ARRAY_SELECT($defaultSet, $(i,item) ARRAY_INDEX($item, 3));
  	LET $previous:= ARRAY_SELECT($previousSet, $(i,item) ARRAY_INDEX($item, 3));
  	LET $match:= ARRAY_INTERSECT($previous, $default); //previous should be first argument as we want to preserve the order/equence of previous settings
  	LET $new:= ARRAY_WHERE($default, $(i,item) ARRAY_INDEXOF($previous, $item) <1 );
  	LET $updated := IF( 
	  					HASH_GET($eventargs, "resetOrder") = TRUE, $default, //loop over default for going back to default order
	  					ARRAY_CONCAT($match, $new) //this is updated to preserve the customised order of processes
	  				);

  	RETURN 
  	ARRAY_SELECT(
	  	$updated, $(p,process)
	  	LET $isNew:= ARRAY_INDEXOF($previous, $process)<1;
	  	LET $previousProcessArray:= ARRAY_WHERE($previousSet, $(i,item) ARRAY_INDEX($item, 3)= $process);
	  	LET $defaultProcessArray := ARRAY_WHERE($defaultSet, $(i,item) ARRAY_INDEX($item, 3)= $process);
	  	LET $enabled:= IF( $isNew = TRUE, 
		  					ARRAY_FIRST($defaultProcessArray),
		  					ARRAY_FIRST($previousProcessArray)
	  					);
	  	RETURN 
	  	ARRAY_CONCAT(
		  $enabled,
		  ARRAY_SKIP($defaultProcessArray, 1)		  
		  )
  	)
);

LET $updatedData:=
ARRAY_SELECTMANY( 
  HASH_KEYS($defaultHash), $(s, stage)
	LET $stageExists := ARRAY_INDEXOF( HASH_KEYS($previousSettingsHash), $stage)>0;
	RETURN
  		IF(
	  		$stageExists <> TRUE, 
	  		HASH_GET($defaultHash, $stage), //if it's a new stage grab new processes from default
	  		CALL($updateProcess, $stage)
	  	)
);

RETURN INDEXES_TO_KEYS(ARRAY_TRANSPOSE($updatedData),$columns)

FieldEntry.Formula:
LET $set := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Required Document],
    EACH [Document in Stage]
  )
); 

LET $stages := ARRAY_DISTINCT(EACH [Stage]); 

LET $matches := 
ARRAY_WHERE(
  $set, $(i, item) 
  ARRAY_INDEXOF($stages, ARRAY_INDEX($item, 2)) > 0
);

RETURN 
ARRAY_INDEX(ARRAY_TRANSPOSE($matches), 1)

LET $set := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Required Document],
    EACH [Document in Stage]
  )
); 

LET $stages := ARRAY_DISTINCT(EACH [Stage]); 

LET $matches := 
ARRAY_WHERE(
  $set, $(i, item) 
  ARRAY_INDEXOF($stages, ARRAY_INDEX($item, 2)) > 0
);

RETURN 
ARRAY_INDEX(ARRAY_TRANSPOSE($matches), 2)
