Column.ValidationFormula:
IF(
  $thisValue = NULL(), $thisColumn & " is required",
)

LET $alreadyExists := DB_LOOKUP( "RecordID", "Change of Role", "[LineNumber] = 1 AND [Role] = " & DB_ENCODECONSTANT([Role]) & " AND [Status] = 'Draft'", HASH( "parent", ARRAY(EFormType, RecordID), "sort", "[DateCreated] DESC" ) ) <> NULL();
LET $text := IF( ISBLANK([Individual Display Name in Role]), "allocated", "reallocated" );
RETURN IF( ISBLANK([Role]), "Role cannot be empty",
$alreadyExists = TRUE, "This role is already being " & $text & " for this record", TRUE )

LET $potentialVendor := EACH [Potential Vendor Name];

RETURN
IF( 
	ARRAY_INDEXOF($potentialVendor, [Vendor Company Name]) > 0,
	"Vendor already added"
)

LET $match:= DB_LOOKUP( "Company Name", "Vendor", "[LineNumber] FAST_EQUAL_TO 1 AND [Company Name Val] = "& DB_ENCODECONSTANT(UPPER($thisValue)), );
LET $potentialVendors := EACH [Potential Vendor Name];
RETURN IF( ARRAY_INDEXOF($potentialVendors, [New Vendor Company Name]) > 0, "Vendor already added",
ISBLANK([New Vendor Company Name]), "Company Name required",
$match <> NULL(), "A vendor with the same name exists already", TRUE )

LET $match:= DB_LOOKUP( "Company Name", "Vendor", "[LineNumber] =1 AND [BusinessNumber Val] = "& DB_ENCODECONSTANT(REPLACESUBSTRING(" ",$thisValue,"")), );
RETURN IF( ISBLANK([New Vendor BusinessNumber]), "BusinessNumber required",
$match <> NULL(), "A vendor with the same BusinessNumber exists already", TRUE )

Column.Formula:
LET $attached := 
IF( 
  [Documentation View] = TRUE, 

  DB_LOOKUP(
    "Document Attached", 
    "Document Upload", 
    HASH("RecordID", [Document Reference RecordID])
  ), 

  [Document Attached to Reference]
); 

RETURN
IF( 
  [Document Name] <> NULL, 
  ARRAY_INDEXOF(
    ARRAY(
      NOT(ISBLANK([TRIM Link to Document]#[LineNumber])),	
      $attached
    ), TRUE
  ) > 0
)

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Stage Document Required], 
    EACH [Document Attached to Reference]
  )
);

LET $remainingThisStage := 
ARRAY_WHERE(
	$data, $(i, item)
	AND(
		ARRAY_INDEX($item, 1) = [Navigation],
		ARRAY_INDEX($item, 2) <> TRUE
	)
);

RETURN ARRAY_LENGTH($remainingThisStage)

Column.ValidationFormula:
LET $status:= ARRAY_LAST($thisValue);
RETURN IF( ISBLANK($thisValue), TRUE,
$status = "Draft", "Exemption Request is in draft", $status = "Submitted", "Exemption Request is pending approval", TRUE )

Column.Formula:
ARRAY_LENGTH(
	EACH [Successful Vendor RecordID]
)

LET $result := [Value Risk Assessment Result];
LET $exists := [Value Risk Assessment Exists];

RETURN 
IF( 
  AND(
	$result <> NULL,
	$result <> "Not completed",
    $result <> "Not started"
  ), 
    $result,

  $exists = TRUE, "Not completed",

  "Not started"
)

LET $result := [Initial Assessment Result];
LET $exists := [Initial Assessment Exists];
LET $complete := HASH_GET([Process Completion Hash - Stored], "Initial Assessment") = TRUE;

RETURN 
IF( 
  $complete = TRUE, $result,
  $exists = TRUE, "Not completed",
  "Not started"
)

Column.ValidationFormula:
LET $poVendor := EACH [Successful Vendor Name];

RETURN
IF( 
	ARRAY_INDEXOF($poVendor, [Vendor Company Name]) > 0,
	"Vendor already added"
)

Column.Formula:
LET $columnSet := ARRAY( "Vendor RecordID", "Vendor Name", "Vendor BusinessNumber", "PO Number", "Award Amount" );
LET $set := ARRAY_TRANSPOSE( ARRAY( EACH [Successful Vendor RecordID], EACH [Successful Vendor Name], EACH [Successful Vendor BusinessNumber], EACH [PO Number], EACH [Award Amount] ) );
LET $set:= ARRAY_WHERE( $set, $(i, item) ARRAY_FIRST($item) <> NULL() );
RETURN ARRAY_SELECT( $set, $(p,podata) INDEXES_TO_KEYS($podata, $columnSet) )

Column.DefaultValueFormula:
LET $size := IF([Status] = "Setup", 30, 28); 
LET $paddingLeft := IF([Status] = "Setup", 5, 6); 
RETURN 
"<image 
  src=""files/public/green-tick.png""; 
  style=""cursor: pointer; width: " & $size & "px; height: " & $size & "px; padding-left: " & $paddingLeft & "px;"">
</image>"

LET $width := IF([Status] = "Setup", 25, 23); 
LET $paddingLeft := IF([Status] = "Setup", 5, 6); 
RETURN 
HASH(
  "Div Style",
  """cursor: pointer; width: " & $width & "px;height: " & $width & "px;margin-left: " & $paddingLeft & "px;border-radius: 50%;border: 3px solid #7b7b7b;text-align: center;font-size: 20px;color: #7b7b7b;font-weight: 600;line-height: 23px;""",


  "Span Style",
  """cursor: pointer; text-align: center;font-size: 20px;color: #7b7b7b;font-weight: 600;line-height: 23px;"""
)

Column.Formula:
LET $thisProcessSteps := [This Stage Process List];//HASH_GET([Process List], [Navigation]);

RETURN
IF( 
  //Prevent steps loading a different set when navigation to evaluation
  [Navigation] = "Evaluate",
  	[Visible Steps],

  //Remember the visible steps so that if we nav back to the process that it will be displayed immediately 
  [Documentation View] = TRUE, 
    [Visible Steps],

  //prevent step from wiping when navigating from documentsview to process view
  $thisProcessSteps = NULL, 
  	[Visible Steps],

  //when navigating between processes this will set to process to match the first process in the selected process
  ARRAY_INDEXOF($thisProcessSteps, ARRAY_FIRST([Visible Steps])) <> NULL,
    [Visible Steps], 

  AND(
	[Wizard Status] = "Not Started",
	[Status] = "Setup"
  ), 
  NULL,

  ARRAY_FIRST($thisProcessSteps)
)

LET $roles := EACH [Role];

LET $allocatedRoles := 
ARRAY_WHERE(
  $roles, $(i, item) 
  [Individual in Role]#$i <> NULL
);

RETURN 
ARRAY_LENGTH(
  ARRAY_WHERE(
    [Required Roles], $(i, item) 
    ARRAY_INDEXOF($allocatedRoles, $item) = 0
  )
) = 0

LET $stageList := [Stage List];

LET $currentStage := [Navigation];

LET $currentStageIndex := ARRAY_INDEXOF($stageList, $currentStage); 

LET $nextStage := ARRAY_INDEX($stageList, $currentStageIndex + 1);

RETURN $nextStage

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Event], 
	EACH [Event Date],
	EACH [Integration Purpose] 
  )
);

LET $advertisingDate := 
ARRAY_INDEX(
  ARRAY_FIRST(
    ARRAY_WHERE(
      $data, $(i, item) 
      OR(
        UPPER(ARRAY_INDEX($item, 1)) = UPPER("Advertising Date"), 
    	ARRAY_INDEX($item, 3) = "Advertising Date"
      )
    )
  ), 2
); 

RETURN 
IF(
  AND(
	ARRAY_INDEXOF([Visible Steps], "Download Responses") > 0, 
    [Outgoing - Release Date] = NULL 
  ),
    $advertisingDate, 
  [Outgoing - Release Date]  
)

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Event], 
	EACH [Event Date],
	EACH [Integration Purpose] 
  )
);

LET $closingDate := 
ARRAY_INDEX(
  ARRAY_FIRST(
    ARRAY_WHERE(
      $data, $(i, item) 
      OR(
        UPPER(ARRAY_INDEX($item, 1)) = UPPER("Procurement Activity Closing Date"), 
    	ARRAY_INDEX($item, 3) = "Closing Date"
      )
    )
  ), 2
); 

RETURN 
IF(
  AND(
	ARRAY_INDEXOF([Visible Steps], "Download Responses") > 0, 
    [Outgoing - Closing Date] = NULL 
  ),
    $closingDate, 
  [Outgoing - Closing Date]  
)

LET $currentProcesses := [This Stage Process List];

LET $allPreviousStepsComplete := $(contextStepName)(
  LET $previousSteps := ARRAY_TAKE([This Stage Process List], ARRAY_INDEXOF([This Stage Process List], $contextStepName) - 1);

  LET $completed := 
  ARRAY_SELECT(
	$previousSteps, $(i, item)
	HASH_GET(HASH_GET([Process Properties], $item), "Completion") = TRUE
  );

  LET $result :=  
  IF( 
	ARRAY_INDEXOF($completed, FALSE) > 0,
	FALSE, 
	TRUE
  );

  RETURN $result
); 

LET $previousStepComplete := $(contextStepName)(
  LET $previousStep := ARRAY_INDEX([This Stage Process List], ARRAY_INDEXOF([This Stage Process List], $contextStepName) - 1);
  LET $result := 
  IF( 
    $previousStep = NULL, 
	TRUE, 

	HASH_GET(HASH_GET([Process Properties], $previousStep), "Completion") = TRUE
  ); 
  RETURN $result
); 

LET $accessible := 
ARRAY_SELECT(
  [This Stage Process List], $(i, item) 
  IF( 
	ARRAY_INDEXOF([Processes Accessed], $item) <= 0,
	  CALL($allPreviousStepsComplete, $item) = TRUE,

	TRUE, 
	OR(
	  ARRAY_INDEXOF([Processes Accessed], $item) > 0,
	  CALL($previousStepComplete, $item) = TRUE
	)
  )
); 

LET $lastAccessibleIndex := ARRAY_LENGTH($accessible) - ARRAY_INDEXOF(ARRAY_REVERSE($accessible), TRUE) + 1;

LET $accessible := 
//this applies some logical adjustments to ensure that there isn't anything inaccessible when it should be accessible
ARRAY_SELECT(
  $accessible, $(i, item) 
  IF( 
	//if the procurement is up to a later stage then all processes on this stage should be accessible
	ARRAY_INDEXOF([Stage List], [Navigation]) < [Current Stage Number], 
	  TRUE,

	//earlier processes should always be accessible if a later process is accessible
    $lastAccessibleIndex > $i, 
	  TRUE,

    $item
  )
);

RETURN 
INDEXES_TO_KEYS($accessible, $currentProcesses)

IF( OR( [Navigation] = "Setup", [Navigation] = "Default", [Navigation] = "Initiate", ), [Wizard Questions],
//don't refresh the list when navigating to evaluations form [Navigation] = "Evaluate", [This Stage Process List],
OR( [Navigation] = "Plan", [Navigation] = "Advertise", [Navigation] = "Award" ), HASH_GET([Process List], [Navigation]) )

Column.DefaultValueFormula:
LET $columns := ARRAY( "Owns Integrated Module - Procurement Planning", "Owns Integrated Module - Evaluations", "Owns Integrated Module - Contracts", "Owns Integrated Module - Go To Market", "Owns Integrated Module - Purchase Orders" );
LET $data := DB_LOOKUP( $columns, "Global Settings", "[RecordID] = 1" );
RETURN INDEXES_TO_KEYS($data, $columns)

LET $ownsAdvancedEvaluations := DB_LOOKUP("Owns Integrated Module - Evaluations", "Global Settings", "[RecordID] = 1 AND [LineNumber] = 1");

RETURN 
	IF($ownsAdvancedEvaluations = TRUE, "VendorPanel Evaluations", "Other")

Column.Formula:
LET $map := HASH( "1 day", ARRAY(1, "day"), "2 days", ARRAY(2, "day"), "3 days", ARRAY(3, "day"), "4 days", ARRAY(4, "day"), "5 days", ARRAY(5, "day"),
"1 week", ARRAY(1 * 7, "day"), "2 weeks", ARRAY(2 * 7, "day"), "3 weeks", ARRAY(3 * 7, "day"), "4 weeks", ARRAY(4 * 7, "day"), "6 weeks", ARRAY(6 * 7, "day"),
"2 months", ARRAY(2, "month"), "3 months", ARRAY(3, "month"), "4 months", ARRAY(4, "month"), "5 months", ARRAY(5, "month"), "6 months", ARRAY(6, "month") );
LET $match := HASH_GET($map, [Evaluation Period]); LET $result := IF( [Outgoing - Closing Date] <> NULL, DATEADD( ARRAY_INDEX($match, 2), ARRAY_INDEX($match, 1), [Outgoing - Closing Date] ) );
RETURN $result

LET $title := [Procurement Title];
LET $len := LEN($title);
LET $limit := 60; 
LET $suffix := IF([Status] = "Cancelled", " - Cancelled");
LET $transformed := IF($len > $limit, LEFT($title, $limit) & "...", $title); 
RETURN COALESCE($transformed, "<Untitled Procurement>") & $suffix

LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName:= HASH(
  	"Insurance",	"Compliance"
  );

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF( $targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title:= 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID)
( EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

	  LET $parent := 
	  IF( 
		[RecordID] > 0, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				  )

			  )
	  )); 

	  LET $gParent := 
	  IF( 
		 $parent <> NULL, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
			  )
		  )
	  )); 

	  LET $hash :=  
	  HASH_MERGE(
		  IF( $parent <> NULL, HASH("parent", 	$parent)),
		  IF( $gParent <> NULL, HASH("gparent", 	$gParent))
	  );

	  LET $parentStructure := 	//2nd instance
	  IF( 
		  ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
	  );

	  LET $parentData:= 
	  ARRAY_WHERE(
		ARRAY(
			$gParent, 
			$parent, 
			ARRAY_CONCAT($current, $currentDes, $currentAccess),
		), $(i,item) $item <> NULL
	   );


	  LET $parentChain:=
		ARRAY_SELECT(
			  $parentData, $(i, item)
			  ARRAY_TAKE($item, 2)
	   );


	  LET $parentLinks :=
	  ARRAY_SELECT(
		  $parentData, $(i, item) 
			  LET $eFormType 		:= ARRAY_FIRST($item);
			  LET $recordID 		:= ARRAY_INDEX($item, 2);
			  LET $descriptiveLabel := ARRAY_INDEX($item, 3);
			  LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
			  LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
			  LET $showEFormType := $eFormType <> "Vendor";

			  LET $labelColour := 
				  IF(
				   AND($eFormType = [EFormType], $recordID = [RecordID]),
					  "background: #ffffff; color: #5D5D5D;",
					 $hasReadAccess = TRUE,
					  "background: #ffffff; color: #4D87B7;",
					  "background: #ffffff; color: #5D5D5D;"

				  );



			  LET $labeltext:= 
					  IF( $showEFormType == TRUE, " " & COALESCE(HASH_GET($altFormName, $eFormType), $eFormType) ) &
					  IF(NOT(ISBLANK($descriptiveLabel)), " " & $descriptiveLabel);

			  LET $label :=  
				  "<span style=""margin-left: 5px;" & $labelColour & """>" &
				  HTMLENCODE($labeltext) & 
				  "</span>";

			  RETURN 
			  IF( 
				  $hasReadAccess == TRUE, 
				  "<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
				  $label 
			  )
	  );

	   LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 18px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";

	  LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

	  RETURN 
	  HASH(
		  "Parent Structure",	$parentStructure,
		  "Parent Data", 		$parentData,
		  "Parent Chain",		$parentChain,
		  "Breadcrumb",		$breadcrumb
		)
	 )
)

Column.ValidationFormula:
(
  LET $inSourcingTeam := ARRAY_INDEXOF([User to Group Mappings], "Sourcing Team") > 0; 
  LET $lockedToSoucingTeam := HASH_GET([SETTINGS], "Market Request - Locked to Sourcing Team") = TRUE;
  RETURN 
  IF( 
	AND(
	  $lockedToSoucingTeam = TRUE, 
	  $inSourcingTeam != TRUE
	), 
	"Only a user in the Sourcing team can post to market",

	AND(
	  $lockedToSoucingTeam != TRUE, 	
	  ARRAY_FIRST([User to Group Mappings]) <> [Procurement Officer]
	),
	"Only the procurement officer can post to market"
  )
)

