LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Role],
	EACH [Individual Display Name in Role]
  )
);

LET $data := 
ARRAY_WHERE(
  $data, $(i, item)  
  ARRAY_INDEX($item, 1) <> NULL
);

RETURN $data

LET $questions := 
DB_LOOKUPALL(
  ARRAY(
    "Question Category", 
    "Question", 
	"Question Value", 
	//"Total Category Points",
	"Answer", 
	"Points"
  ),
  "Standard Question", 
  "[Status] <> 'Deleted' AND [Status] <> 'Deleted' AND [Type] = 'Evaluation Criteria' AND [Question Category] IS NOT EMPTY", 
  HASH("ancestor", ARRAY(EFormType, RecordID))
); 

LET $questions := 
ARRAY_SELECT(
  $questions, $(i, item)

  LET $matches := ARRAY_WHERE($questions, $(i, search) ARRAY_INDEX($search, 2) = ARRAY_INDEX($item, 2));  
  LET $maxScore := GREATEST(ARRAY_SELECT($matches, $(i, item) ARRAY_INDEX($item, 5)));
  LET $thisScore := ARRAY_INDEX($item, 5); 

  RETURN 
  ARRAY_CONCAT(
	ARRAY_TAKE($item, 4),
	NUMBERFORMAT($thisScore / $maxScore, "P")
  )
); 

LET $distinctCategories := ARRAY_DISTINCT(ARRAY_SELECTMANY($questions, $(i, item) ARRAY_TAKE($item, 1)));


LET $answersHashed := 
ARRAY_SELECT(
  $distinctCategories, $(i, category)
  LET $filteredQuestions := ARRAY_WHERE($questions, $(i, item) ARRAY_INDEX($item, 1) = $category);
  RETURN
  ARRAY_TO_HASH_GROUPED(
    $filteredQuestions, 
    $(i, item) ARRAY_INDEX($item, 2), 
    $(i, item) ARRAY_SKIP($item, 3)
  )
); 

LET $questionDataHashed := INDEXES_TO_KEYS($answersHashed, $distinctCategories); 

LET $priceCriteria := 
DB_LOOKUP(
  ARRAY(
	"Price Weighting", 
	"Price Weighting Percent",
	"Evaluating Teams - Price",
    "Price Scoring Method"
  ), 
  "Evaluation", 
  "[Criteria Name] IS NOT NULL", 
  HASH(
	"parent", ARRAY(EFormType, RecordID)
  )
);  

LET $criteriaData := 
DB_LOOKUPALL(
  ARRAY(
    "Criteria Name", 
	"Evaluating Teams", 
	"Weighting Percent"	
  ), 
  "Evaluation", 
  "[Criteria Name] IS NOT NULL", 
  HASH(
	"parent", ARRAY(EFormType, RecordID)
  )
);

LET $teamData := 
DB_LOOKUPALL(
  ARRAY(
    "Team Name", 
	"Team UUID"
  ), 
  "Evaluation", 
  "[Team Name] IS NOT NULL", 
  HASH(
	"parent", ARRAY(EFormType, RecordID)
  )
);

LET $teamDataHashed := 
INDEXES_TO_KEYS(
  ARRAY_INDEX(ARRAY_TRANSPOSE($teamData), 1),
  ARRAY_INDEX(ARRAY_TRANSPOSE($teamData), 2)
); 

LET $categoryQuestionValues := 
ARRAY_TO_HASH_GROUPED(
  ARRAY_DISTINCT($questions), 
  $(i, item) ARRAY(ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2)), 
  $(i, item) ARRAY_INDEX($item, 3)
);

LET $questionValues := 
ARRAY_TO_HASH_GROUPED(
  ARRAY_DISTINCT($questions), 
  $(i, item) ARRAY_INDEX($item, 2), 
  $(i, item) ARRAY_INDEX($item, 3)
);

LET $criteriaPointValue := 
(
  LET $set := 
  ARRAY_SELECT(
    HASH_KEYS($categoryQuestionValues), $(i, key) 
    ARRAY(
  	$key, 
      ARRAY_FIRST(HASH_GET($categoryQuestionValues, $key))
    )
  );

  LET $set2 :=  
  ARRAY_SELECT(
    $distinctCategories, $(i, category) 
    LET $matches := ARRAY_WHERE(
  	$set, $(i, item)
  	ARRAY_INDEX(ARRAY_INDEX($item, 1), 1) = $category
    );

    LET $values := 
    ARRAY_SELECT($matches, $(i, item) ARRAY_INDEX($item, 2));

    RETURN 
    ARRAY(
  	$category, 
      SUM($values)
    )
  );

  LET $result :=  
  INDEXES_TO_KEYS(
    ARRAY_INDEX(ARRAY_TRANSPOSE($set2), 2), 
    ARRAY_INDEX(ARRAY_TRANSPOSE($set2), 1)
  );

  RETURN $result
); 

LET $result :=  
ARRAY_SELECT(
  $criteriaData, $(i, criteria)

  LET $criteriaName := ARRAY_INDEX($criteria, 1); 
  LET $evaluatingTeamUUID := ARRAY_INDEX($criteria, 2);
  LET $criteriaWeight := ARRAY_INDEX($criteria, 3); 
  LET $evaluatingTeamName := ARRAY_JOIN(ARRAY_SELECT($evaluatingTeamUUID, $(i, item) HASH_GET($teamDataHashed, $item)), ", ");
  LET $questionData := HASH_GET($questionDataHashed, $criteriaName); 

  LET $questionDataFormatted := 
  ARRAY_SELECT(
    HASH_KEYS($questionData), $(i, item)
    LET $data := HASH_GET($questionData, $item);
	LET $questionValue := ARRAY_FIRST(HASH_GET($questionValues, $item));
	LET $categoryValue := SUM(HASH_GET($criteriaPointValue, $criteriaName));
	LET $questionWeight := NUMBERFORMAT($questionValue / $categoryValue, "P");

    RETURN
  	ARRAY(
  	  $item,
	  $questionWeight,
  	  $data
  	)
  );

  RETURN 
  ARRAY(
	$criteriaName, 
	NUMBERFORMAT($criteriaWeight / 100, "P"),
    $evaluatingTeamName, 
	$questionDataFormatted
  )
);

LET $priceScoringMethod := ARRAY_INDEX($priceCriteria, 4);

RETURN 
ARRAY_CONCAT(
  ARRAY(
	ARRAY(
	  "Price",
	  NUMBERFORMAT(ARRAY_INDEX($priceCriteria, 2) / 100, "P"), 
	  IF($priceScoringMethod = "Automatic", "Calculated", ARRAY_INDEX($priceCriteria, 4)), 
	  ARRAY(
	    ARRAY(
		  IF($priceScoringMethod = "Automatic", "<Price will be automatically calculated.>"), 
		  IF($priceScoringMethod = "Automatic", NUMBERFORMAT(ARRAY_INDEX($priceCriteria, 2) / 100, "P"))
		)
	  )
    )
  ),
  $result
)

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Checklist - Initiate Item Text],
	EACH [Checklist - Initiate Date],
	EACH [Checklist - Initiate Notes]
  )
);

LET $data := 
ARRAY_WHERE(
  $data, $(i, item)  
  ARRAY_INDEX($item, 1) <> NULL
);

RETURN $data

!
LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Extension Years],
    EACH [Extension Months],
    EACH [Extension Days]
  )
);

LET $distinct := ARRAY_DISTINCT($data);

LET $result :=  
ARRAY_SELECT(
  $distinct, $(i, distinctItem) 
  (
    LET $matches := ARRAY_LENGTH(ARRAY_WHERE($data, $(i, item) $item = $distinctItem)); 
	LET $years := ARRAY_INDEX($distinctItem, 1); 
	LET $months := ARRAY_INDEX($distinctItem, 2); 
	LET $days := ARRAY_INDEX($distinctItem, 3); 

	LET $sentenceParts := 
	ARRAY(
	  IF($years > 0, $days & " years"), 
	  IF($months > 0, $months & " months"), 
	  IF($days > 0, $days & " days")
    );

	LET $sentence := ARRAY_JOIN(ARRAY_WHERE($sentenceParts, $(i, item) $item <> NULL), ", ");

	RETURN $matches & " x " & $sentence
  )
);

RETURN ARRAY_JOIN($result, NEWLINE())

LET $questions := 
DB_LOOKUPALL(
  ARRAY(
    "Question Category", 
    "Question"  
  ),
  "Standard Question", 
  "[Type] = 'Gateway Criteria' AND [Question Category] IS NOT EMPTY AND [LineNumber] = 1", 
  HASH("ancestor", ARRAY(EFormType, RecordID))
); 

LET $questionsHashed := 
ARRAY_TO_HASH_GROUPED(
  $questions, 
  $(i, item) ARRAY_INDEX($item, 1), 
  $(i, item) ARRAY_INDEX($item, 2)
); 

LET $criteriaData := 
DB_LOOKUPALL(
  ARRAY(
    "Gateway Criteria Name"
  ), 
  "Evaluation", 
  "[Gateway Criteria Name] IS NOT NULL", 
  HASH(
	"parent", ARRAY(EFormType, RecordID)
  )
);

LET $result := 
ARRAY_SELECT(
  $criteriaData, $(i, item)
  LET $criteria := ARRAY_FIRST($item);
  LET $questions := HASH_GET($questionsHashed, $criteria);

  RETURN 
  ARRAY(
	$criteria, 
	$questions
  )
);

RETURN $result

