  	//check if the visible step is "Award" - this is the default process shown if the Award Stage is disabled
  	ARRAY_INDEXOF([Visible Steps], "Award") > 0,

  	//if Create Award is displayed, check if Market Status was populated (from Advertise Stage), or if there is no output type selected
  	AND(
		ARRAY_INDEXOF([Visible Steps], "Create Award") > 0,
	  	OR(
    		[Market Status] <> NULL,
		  	[Output] == NULL
		)
	),

  	//if Create Award is displayed, and the Market Status wasn't populated from Advertise, check the Output type, and show this section if the relevant integrated module isn't owned, or if there's a linked RFx
  	AND(
	  	ARRAY_INDEXOF([Visible Steps], "Create Award") > 0,
	  	[Market Status] == NULL,
	  	OR(
		  	NOT(ISBLANK([VendorPanel ID])),
			AND([Output] = "Contract", HASH_GET([Integrated Modules], "Owns Integrated Module - Contracts") <> TRUE), 
			AND([Output] = "Subcontract", HASH_GET([Integrated Modules], "Owns Integrated Module - Contracts") <> TRUE), 
			AND([Output] = "Panel Contract", HASH_GET([Integrated Modules], "Owns Integrated Module - Contracts") <> TRUE), 
			AND([Output] = "Procurement", HASH_GET([Integrated Modules], "Owns Integrated Module - Procurement Planning") <> TRUE), 
			AND([Output] = "Purchase Order", HASH_GET([Integrated Modules], "Owns Integrated Module - Purchase Orders") <> TRUE)
  		)
	)
)

AND(
  ARRAY_INDEXOF([Visible Steps], "Create Award") > 0,
  //Hide award options if the record has a linked RFx
  ISBLANK([VendorPanel ID]),
  //We should also only show this if there is not a separate Market Status (i.e. has been separately marked as "closed")
  [Market Status] = NULL,
  //Check the output type against owned modules
  OR(
	AND([Output] = "Contract", HASH_GET([Integrated Modules], "Owns Integrated Module - Contracts") = TRUE), 
	AND([Output] = "Subcontract", HASH_GET([Integrated Modules], "Owns Integrated Module - Contracts") = TRUE), 
	AND([Output] = "Panel Contract", HASH_GET([Integrated Modules], "Owns Integrated Module - Contracts") = TRUE), 
	AND([Output] = "Procurement", HASH_GET([Integrated Modules], "Owns Integrated Module - Procurement Planning") = TRUE), 
