Navigate.UrlTemplate:
{
LET $pattern:= "(?:http[s]*\:\/\/)*(.*?)(?=\.)";
LET $url:= EBMS_GET_BASE_URL();

	LET $prefix:= REGEX_MATCH($url, $pattern);

	LET $newprefix := 
	IF( 
	  $prefix = "uat-nimblex", 
	    "https://uat-vp.",

	  EBMS_CONTAINS($prefix, "uat"), 
		"https://" & $prefix & ".", 

	  "https://www."
	);

	RETURN 
	$newprefix & "vendorpanel.com.au/Members/?do=Wizards:RfxWizard&step=1&opportunityId=" & [VendorPanel ID]

}

{
LET $pattern:= "(?:http[s]*\:\/\/)*(.*?)(?=\.)";
LET $url:= EBMS_GET_BASE_URL();
	LET $prefix:= REGEX_MATCH( $url, $pattern);

	LET $newprefix := 
	IF( 
	  $prefix = "uat-nimblex", 
	    "uat-vp.",

	  EBMS_CONTAINS($prefix, "uat"), 
		"https://" & $prefix & ".", 

	  "https://www."
	);

	RETURN 
	$newprefix & "vendorpanel.com.au/Members/?do=Opportunities:AllResponses&opportunityId=" & [VendorPanel ID]

}

FormulaCriterion.Formula:
LET $set :=
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Checklist - Initiate Item],
		EACH [Checklist - Initiate Item Text]
	)
);

LET $filtered := 
ARRAY_WHERE(
	$set, $(i, item) 
	ARRAY_INDEX($item, 2) <> NULL()
);	

LET $trueSet := 
ARRAY_WHERE(
	$filtered, $(i, item) 
	ARRAY_INDEX($item, 1) == TRUE		
);

RETURN 
ARRAY_LENGTH($filtered) == ARRAY_LENGTH($trueSet)

LET $set :=
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Checklist - Initiate Item],
		EACH [Checklist - Initiate Item Text]
	)
);

LET $filtered := 
ARRAY_WHERE(
	$set, $(i, item) 
	ARRAY_INDEX($item, 2) <> NULL()
);	

LET $trueSet := 
ARRAY_WHERE(
	$filtered, $(i, item) 
	ARRAY_INDEX($item, 1) == TRUE		
);

RETURN 
ARRAY_LENGTH($filtered) <> ARRAY_LENGTH($trueSet)

Binding.Expression:
AND(
  //NOT(ISBLANK([Estimated Value])), 
  [Procurement Category] <> [Wizard - Process]
)

LET $data := HASH_GET([Wizard - Process Data], [Wizard - Process]);

LET $highValue := NUMBERFORMAT(HASH_GET($data, "High Value"), "C");
LET $unlimited := HASH_GET($data, "Unlimited");

LET $lowValue := NUMBERFORMAT(HASH_GET($data, "Low Value"), "C");
LET $highValue := IF($unlimited = TRUE, "No limit", $highValue);
LET $output := HASH_GET($data, "Output");

RETURN 
"Value range: " & $lowValue & " to " & $highValue

LET $data := HASH_GET([Wizard - Process Data], [Wizard - Process]);
LET $output := HASH_GET($data, "Output");

RETURN "This process will award a " & $output

LET $data := HASH_GET([Wizard - Process Data], [Wizard - Process]);

LET $highValue := NUMBERFORMAT(HASH_GET($data, "High Value"), "C");
LET $unlimited := HASH_GET($data, "Unlimited");

LET $lowValue := NUMBERFORMAT(HASH_GET($data, "Low Value"), "C");
LET $highValue := IF($unlimited = TRUE, "No limit", $highValue);
LET $output := HASH_GET($data, "Output");

RETURN 
"Lower threshold: " & $lowValue & " | Upper threshold: " & $highValue & " | Award output: " & $output

LET $data := HASH_GET([Wizard - Process Data], [Wizard - Process]);

LET $highValue := HASH_GET($data, "High Value");
LET $unlimited := HASH_GET($data, "Unlimited");

RETURN 
IF( 
  $unlimited <> TRUE,
  [Estimated Value] > $highValue
)

