Binding.Expression:
LET $standardRoles :=
ARRAY(
  "Procurement Officer",
  "Procurement Manager", 
  "Procurement Owner"
);

LET $requiredRoles := [Required Roles]; 

LET $combined := 
ARRAY_CONCAT(
  $standardRoles, 
  $requiredRoles, 
  ARRAY("Other")
);

LET $fullList := ARRAY_DISTINCT(ARRAY_WHERE($combined, $(i, item) $item <> NULL));

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Role], 
    EACH [Individual Display Name in Role]
  )
);

LET $populatedRoles := 
ARRAY_FIRST(ARRAY_TRANSPOSE(ARRAY_WHERE($data, $(i, item) ARRAY_INDEX($item, 2) <> NULL))); 

RETURN 
ARRAY_WHERE(
  $fullList, $(i, selection) 
  ARRAY_INDEXOF($populatedRoles, $selection) == NULL
)

