FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $columns := ARRAY( "Username in Role", "Person in Role", "Role", "Custom Role", "Wizard - Not a System User" );
LET $data := ARRAY_TRANSPOSE(ARRAY( EACH [Username in Role], EACH [Person in Role], EACH [Role], EACH [Custom Role], EACH [Wizard - Not a System User] ));
LET $removeLine := ARRAY_INDEX(SPLIT($clickid, " | "), 2);
LET $modifiedData := ARRAY_CONCAT( ARRAY_TAKE($data, $removeLine - 1), ARRAY_SKIP($data, $removeLine) );
RETURN INDEXES_TO_KEYS( ARRAY_TRANSPOSE($modifiedData), $columns )

Binding.Expression:
LET $columns := ARRAY("Role", "Individual Display Name in Role", "LineNumber"); 

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Role], 
    EACH [Individual Display Name in Role]
  )
);

LET $hashedData := ARRAY_SELECT($data, $(i, item) HASH_MERGE(INDEXES_TO_KEYS($item, $columns), HASH("LineNumber", $i)));

LET $populatedRoles := ARRAY_WHERE($hashedData, $(i, item) HASH_GET($item, "Individual Display Name in Role") <> NULL);

LET $rows := 
ARRAY_SELECT(
