FieldEntry.Formula:
LET $timelineID:= 
DB_LOOKUP(
  "Custom Timeline RecordID",
  "Procurement Process",
  HASH("RecordID", [Procurement Process RecordID], "LineNumber", 1)
);

LET $filter:= 
IF(
  $timelineID = NULL(), "[RecordID] = 1",
  "[RecordID] = " &DB_ENCODECONSTANT($timelineID)
);

LET $lookup:= 
DB_LOOKUPALL(
  ARRAY(
    "Event",
    "Order",
    "Week",
    "Day",
    "Integration Purpose", 
    "Days Relative to Advertising Date", 
    "Specify Weekday", 
    "Skip Weekends"
  ),
  "Procurement Timeline",
  $filter
);

LET $today := [Target Advertising Date];
LET $dayOfWeek := DAYOFWEEK($today);

//Rebase the advertising date based on what is specified in the timeline
LET $advertisingData := ARRAY_FIRST(ARRAY_WHERE($lookup, $(i, item) ARRAY_INDEX($item, 5) = "Advertising Date")); 


LET $today := 
IF( 
  AND(
    ARRAY_INDEX($advertisingData, 7) = TRUE, 
    ARRAY_INDEX($advertisingData, 4) <> NULL
  ), 
  DATEADD(
    "day", 
    MOD(ARRAY_INDEX($advertisingData, 4) + 7 - $dayOfWeek, 7),// + 
      //(7 * ARRAY_INDEX($advertisingData, 3) * ARRAY_INDEX($advertisingData, 2)), 
    $today
  ),

  [Target Advertising Date]
);

LET $dayOfWeek := DAYOFWEEK($today);

LET $shiftDay := $(date, direction)
(
  LET $result := 
  IF( 
	$direction = "back", 
	DATEADD("day", -1, $date), 

	$direction = "forward", 
	DATEADD("day", 1, $date)
  ); 

  RETURN 
  IF( 
    NOT(ISWEEKDAY($result)), 
	  CALL($shiftDay, $result, $direction), 
	$result
  )
); 

LET $shiftWeek := $(date, direction)
(
  DATEADD("day", 7 * $direction, $date)
);

LET $list:= 
ARRAY_SELECT(
  $lookup,
  $(i,item)
  LET $event:= ARRAY_INDEX($item, 1);
  LET $order:= ARRAY_INDEX($item, 2);
  LET $weekNumber:= ARRAY_INDEX($item, 3);
  LET $day:= COALESCE(ARRAY_INDEX($item, 4),$dayOfWeek) + 7;
  LET $integrationPurpose := ARRAY_INDEX($item, 5);
  LET $daysRelativeToAdvertising := ARRAY_INDEX($item, 6);
  LET $specifyWeekday:= ARRAY_INDEX($item, 7);
  LET $skipweekends := ARRAY_INDEX($item, 8); 
  LET $minimumDaysToHitTargetDayOfWeek := MOD($day - $dayOfWeek, 7);
  LET $rollForward := CEILING(($daysRelativeToAdvertising * $order), 7);
  LET $rollBack := FLOOR(($daysRelativeToAdvertising * $order), 7);

  LET $targetDate := 
  IF( 
    $integrationPurpose = "Advertising Date", 
      $today, 

    AND(
	  ARRAY_INDEX($item, 4) <> NULL, 
	  $specifyWeekday == TRUE
	), 

    DATEADD(
      "day",
  	  MOD($day - $dayOfWeek, 7) + 
  	  IF(
  	    AND(
  		  $order = -1,
  		  -($minimumDaysToHitTargetDayOfWeek) + $rollForward >= -($daysRelativeToAdvertising)
  		), 
  		$rollForward,

  		$order = -1, 
  		  $rollBack,

  	    AND(
  		  $order = 1,
  		  $minimumDaysToHitTargetDayOfWeek + $rollBack >= $daysRelativeToAdvertising
  		), 
  		  $rollBack,

  		$rollForward
      ), 
  	  $today
    ),

    DATEADD(
      "day",
  	$daysRelativeToAdvertising * $order, 
  	$today
    )
  );

  //move back or forwards if date is landing on a weekend, recursive
  LET $targetDate := 
  IF(
    AND($skipweekends = TRUE, $order = -1, NOT(ISWEEKDAY($targetDate))), 
      CALL($shiftDay, $targetDate, "back"), 

    AND($skipweekends = TRUE, OR($order = 0, $order = 1), NOT(ISWEEKDAY($targetDate))), 
      CALL($shiftDay, $targetDate, "forward"), 

    $targetDate
  ); 

  //Move a week back if min. days not met
  LET $targetDate := 
  IF( 
    AND(
      $order = -1, 
  	$today - $targetDate < $daysRelativeToAdvertising
    ), 
      CALL($shiftWeek, $targetDate, -1), 
    $targetDate
  );

  RETURN
  ARRAY(
    $integrationPurpose,
    $event,
    $targetDate
  )
);

RETURN ARRAY_TRANSPOSE($list)

Binding.Expression:
LET $step := "Timeline";
LET $lockedByApproval := ARRAY_INDEXOF([This Stage Process List], "Procurement Plan Approval") > ARRAY_INDEXOF([This Stage Process List], $step);
LET $inProgress := HASH_GET([Submission Data], "In Progress");
LET $approvalInProgress := 
OR( 
  ARRAY_INDEXOF($inProgress, "Procurement Plan") > 0
);
LET $isNotCurrentStage:= [Navigation] <> [Current Stage];
RETURN 
OR(
  $approvalInProgress,
  $isNotCurrentStage,
  AND( [Plan Approval Complete] = TRUE,  $lockedByApproval = TRUE)
)

LET $step := "Timeline";
RETURN 
AND(
  ARRAY_INDEXOF([Visible Steps], $step) > 0
)

LET $step := "Evaluation Planning";
