Binding.Expression:
LET $step := "Procurement Plan Approval";

LET $targetType := "Procurement Plan"; 
LET $targetStatus := "Draft"; 

LET $data := HASH_GET([Submission Data], "Raw");

LET $matches := 
ARRAY_WHERE(
  $data, $(i, item) 
  AND(
	HASH_GET($item, "Type") = $targetType,
    HASH_GET($item, "Status") = $targetStatus,
  )
);  

LET $submissions := 
ARRAY_WHERE(
  $data, $(i, item) 
  AND(
	HASH_GET($item, "Type") = $targetType
  )
);   

LET $numberInDraft := ARRAY_LENGTH($matches); 
LET $numberOfSubmission := ARRAY_LENGTH($submissions); 

RETURN
AND(
  OR(
    $numberInDraft = 0, 
  ),
  ARRAY_INDEXOF([Visible Steps], $step) > 0
)

