FormulaCriterion.Formula:
LET $set :=
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Checklist - Plan Item],
		EACH [Checklist - Plan Item Text]
	)
);

LET $filtered := 
ARRAY_WHERE(
	$set, $(i, item) 
	ARRAY_INDEX($item, 2) <> NULL()
);	

LET $trueSet := 
ARRAY_WHERE(
	$filtered, $(i, item) 
	ARRAY_INDEX($item, 1) == TRUE		
);

RETURN 
ARRAY_LENGTH($filtered) == ARRAY_LENGTH($trueSet)

LET $set :=
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Checklist - Plan Item],
		EACH [Checklist - Plan Item Text]
	)
);

LET $filtered := 
ARRAY_WHERE(
	$set, $(i, item) 
	ARRAY_INDEX($item, 2) <> NULL()
);	

LET $trueSet := 
ARRAY_WHERE(
	$filtered, $(i, item) 
	ARRAY_INDEX($item, 1) == TRUE		
);

RETURN 
ARRAY_LENGTH($filtered) <> ARRAY_LENGTH($trueSet)

