LET $suppliers := HASH_GET([Supplier Selection], "Suppliers");
LET $listHash := 
	ARRAY_TO_HASH(
  			HASH_GET([Supplier Selection], "Lists"),
	  			$(i,item) HASH_GET($item, "Id"), //list Id is key
	  			$(i,item) $item
  		);
//LET $requiredRoles := [Required Roles];

RETURN 
"<div style='margin-left:5px; padding:3px 0px 8px 0px; height: 10px;'>You have selected the following suppliers:</div>"&
"<div style='padding-top: 3px; height: 100px; overflow: auto;'>" & 
"<ol style='line-height: 18px'>" & 
ARRAY_JOIN(
  ARRAY_SELECT(
	$suppliers, $(i,item)
	//LET $supplierInfo:= HASH_GET($item, "SupplierInfo");
	LET $lists:=  ARRAY_SELECT(
	  					HASH_GET($item, "SupplierListIds"), $(i, listId)
	  					LET $listInfo:= HASH_GET($listHash, $listId);
	  					RETURN "List: "& HASH_GET($listInfo, "Name") & " | List owner: "& HASH_GET($listInfo, "Owner")	
	  			);

  	LET $name := "<strong>"& HTMLENCODE(HASH_GET($item, "Name")) &"</strong>" & " ("& HTMLENCODE(ARRAY_JOIN($lists, "; "))& ") ";
	RETURN
		"<li>" & $name & 
		//IF(EVALUATE("[" & $item & " Display Name]") <> NULL, "<span style='padding-left: 5px;'>" & $tick & "</span>") & 
		"</li>"
  ), 
  "") & 
"</ol>" &
"</div>"

