FieldEntry.Formula:
LET $existing := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Potential Vendor BusinessNumber],
		EACH [Potential Vendor Name],
		EACH [Potential Vendor RecordID]
	)
);

LET $newSet := 
ARRAY(
	[New Vendor BusinessNumber],
	[New Vendor Company Name],
	NULL()
);

LET $combined := 
ARRAY_CONCAT(
	$existing, 
	ARRAY($newSet)
);

LET $filtered := 
ARRAY_WHERE(
	$combined, $(i, item) 
	ARRAY_INDEX($item, 2) <> NULL()
);

LET $result := ARRAY_DISTINCT($filtered);

RETURN 
$result

