  "columns", 
  ARRAY(
    HASH("heading", "Role", "width", 460), 
	HASH("heading", "User", "width", 300), 
	HASH("heading", "", "width", 60), 
	HASH("heading", "", "width", 60)
  ),
  "rows",
  $rows
)

