FieldEntry.Formula:
LET $years := DATEADD("year", [Duration Years], [Start Date]);
LET $months := DATEADD("month", [Duration Months], $years);
LET $days := DATEADD("day", [Duration Days], $months);

LET $days := 
IF( 
	$days > [Start Date], 
	DATEADD("day", -1, $days),
	$days
);

RETURN 
IF( 
	ISBLANK([Start Date]),	
	[Original End Date],
	$days
)

LET $components := 
ARRAY_SELECT(
  ARRAY(
    ARRAY_TRANSPOSE(ARRAY(EACH [Extension Years], EACH [Count of Extensions])),
    ARRAY_TRANSPOSE(ARRAY(EACH [Extension Months], EACH [Count of Extensions])), 
    ARRAY_TRANSPOSE(ARRAY(EACH [Extension Days], EACH [Count of Extensions]))
  ), $(i, item) 
  SUM(
    ARRAY_SELECT(
      $item, $(j, jitem) 
      ARRAY_INDEX($jitem, 1) * ARRAY_INDEX($jitem, 2)		
    )
  )
);

LET $years := ARRAY_FIRST($components);
LET $months := ARRAY_INDEX($components, 2);
LET $days := ARRAY_LAST($components);

LET $calculated := 
IF(
	[Original End Date] == NULL(), NULL(),
	DATE(YEAR([Original End Date]) + $years, MONTH([Original End Date]) + $months, DAY([Original End Date]) + $days)
);
RETURN
IF(
  [Extension Options] <> TRUE, [Original End Date],
  $calculated
)

