      ARRAY_INDEX($jitem, 1) * ARRAY_INDEX($jitem, 2)		
    )
  )
);

LET $years := ARRAY_FIRST($components);
LET $months := ARRAY_INDEX($components, 2);
LET $days := ARRAY_LAST($components);

LET $calculated := 
IF(
	[Original End Date] == NULL(), NULL(),
	DATE(YEAR([Original End Date]) + $years, MONTH([Original End Date]) + $months, DAY([Original End Date]) + $days)
);
RETURN
IF(
  [Extension Options] <> TRUE, [Original End Date],
  $calculated
)

