FormulaCriterion.Formula:
LET $type := "Procurement Initiation";
LET $inDraft := HASH_GET([Submission Data], "In Draft");
LET $inProgress := HASH_GET([Submission Data], "In Progress");

RETURN

AND(
  	ARRAY_INDEXOF($inDraft, $type) <= 0,
  	ARRAY_INDEXOF($inProgress, $type) <= 0
)

LET $type := "Procurement Initiation";
