LET $inProgress := HASH_GET([Submission Data], "In Progress");

RETURN

OR(
  	ARRAY_INDEXOF($inDraft, $type) > 0,
  	ARRAY_INDEXOF($inProgress, $type) > 0,
  	[Approval to Initiate Procurement Complete] == TRUE
)

Binding.Expression:
LET $step := "Approval";

LET $targetType := "Procurement Initiation"; 
LET $data := HASH_GET([Submission Data], "Raw");

LET $inProgress := 
ARRAY_WHERE(
  $data, $(i, item) 
  AND(
	HASH_GET($item, "Type") = $targetType,
	OR(
      HASH_GET($item, "Status") = "Draft",
      HASH_GET($item, "Status") = "Submitted",
      HASH_GET($item, "Status") = "Approved"
	)	
  )
);   
LET $rejected := 
ARRAY_WHERE(
  $data, $(i, item) 
  AND(
    HASH_GET($item, "Type") = $targetType,
	OR(
	  HASH_GET($item, "Status") = "Voided",
	  HASH_GET($item, "Status") = "Rejected",
	  HASH_GET($item, "Status") = "Cancelled"
	)
  )
); 

LET $numberInProgress := ARRAY_LENGTH($inProgress); 
LET $numberRejected := ARRAY_LENGTH($rejected); 

LET $approvalRequired := 
(
  LET $type := $targetType; 
  LET $workflowSettings := HASH_GET([Approval Workflow Settings], $type);
  RETURN NOT(HASH_GET($workflowSettings, "Not Required"))
);

RETURN
AND(
  $approvalRequired = TRUE,
  $numberInProgress = 0,
  $numberRejected > 0,
  ARRAY_INDEXOF([Visible Steps], $step) > 0
)

