LET $css := 
"
<style>
.approvalHistoryDotFilled {
  width: 10px;
  height: 10px;
  border-radius: 10px;
  background: #474747;
  border: 1px solid #474747;
  display: inline-block; 
}
.approvalHistoryDotNotFilled {
  width: 10px;
  height: 10px;
  border-radius: 10px;
  background: white;
  border: 1px solid #474747;
  display: inline-block; 
}
.approvalHistoryLineToNextDot {
  background: #474747;
  width: 1px;
  height: 13px;
  position: absolute;
  left: -7px;
  margin-top: 4px; 
}
.approvalHistoryVoidedPill {
  display: inline;
  background: #f1f1f1;
  padding: 3px 7px 3px 7px;
  border: 1px solid #585858;
  border-radius: 3px;
  margin-left: 10px;
  color: #585858; 
  cursor: pointer;
}
.approvalHistoryApprovedPill {
  display: inline;
  background: white;
  padding: 3px 7px 3px 7px;
  border: 1px solid #2acd40;
  border-radius: 3px;
  margin-left: 10px;
  color: #2acd40; 
  cursor: pointer;
}
.approvalHistoryRejectedPill {
  display: inline;
  background: white;
  padding: 3px 7px 3px 7px;
  border: 1px solid #f90000;
  border-radius: 3px;
  margin-left: 10px;
  color: #f90000;
  cursor: pointer;
}
.approvalHistoryContainer {
  padding: 10px; 
}

</style>";

LET $html :=  
ARRAY_SELECT(
  $matches, $(i, item) 

  LET $lastEventDate := HASH_GET($item, "Last Decision Date");
  LET $lastDecisionMaker := HASH_GET($item, "Last Decision Maker");
  LET $status := HASH_GET($item, "Status");
  LET $recordID := HASH_GET($item, "RecordID");

  LET $lastEventDate := 
  IF(
    $status = "Approved", 
    HASH_GET($item, "Date Approved"),

	$status = "Rejected", 
    HASH_GET($item, "Date Rejected"),

	"In progress"
  );

  LET $decisionMaker := 
  IF(
	$status = "Approved", 
    HASH_GET($item, "Approved By Name"),

	$status = "Rejected", 
    HASH_GET($item, "Rejected By Name"),

	ARRAY_JOIN(ARRAY_SELECT(
	  HASH_GET($item, "Assignee(s)"), $(i, item)
	  EBMS_GET_ROLE_DISPLAYNAME($item)
	))
  ); 

  RETURN
  "<div class='approvalHistoryContainer'>" & 
    IF(
	  $i = 1, 
	  "<div class='approvalHistoryDotFilled'></div>", 
	  "<div class='approvalHistoryDotNotFilled'></div>"
	) &
    IF(
	  $i <> ARRAY_LENGTH($matches), 
	  "<div style='display: inline-block; position: relative'>
	    <div class='approvalHistoryLineToNextDot'></div>
	   </div>"
	) &
    "<span style='padding-left: 20px; padding-right: 10px; font-weight: bold;'>Approval Round " & ARRAY_LENGTH($matches) - $i + 1 & ":</span>" &
	IF( 
	  OR(
		$status = "Approved", 
		$status = "Rejected"
	  ),
	  "<span style='padding-right: 10px;'>" & $lastEventDate & "</span>" &
	  "<span style='padding-right: 20px;'>By: " & $decisionMaker & "</span>"
	)
    &
	IF( 
	  $status = "Voided", 
	  "<div data-clickid=" & $recordID & "  class='approvalHistoryVoidedPill'>" & $status & "</div>",

	  $status = "Approved", 
	  "<div data-clickid=" & $recordID & "  class='approvalHistoryApprovedPill'>" & $status & "</div>",

	  $status = "Rejected", 
	  "<div data-clickid=" & $recordID & " class='approvalHistoryRejectedPill'>" & $status & "</div>"
    ) &
  "</div>"
);

RETURN $css & "<div style='overflow: auto; max-height: 70px;'>" & ARRAY_JOIN($html) & "</div>"

