Settings.LandingViewFormula:
LET $user := EBMS_GET_CURRENT_USER();
LET $roles := EBMS_GET_ANCESTOR_ROLES($user);

LET $userIsInEveryone := ARRAY_INDEXOF($roles, "Everyone") > 0;
LET $userIsInAdministrators := ARRAY_INDEXOF($roles, "Administrators") > 0;
LET $userIsInSystemBuilders := ARRAY_INDEXOF($roles, "System Builders") > 0;
LET $userIsInExternalApprovers := ARRAY_INDEXOF($roles, "External Approvers") > 0;

LET $defaultViewFix := 
IF(
  AND(
	OR(
	  [RecordID] < 0, 
	  [Current Stage] = "Setup"
	),
	$requestedView = "TabularReportRedirect"
  ),
    "Setup",

  OR(
    [Default View] = "Default", 
    [Default View] = "Initiate"
  ),
    "Setup", 
  [Default View]
);

RETURN 
IF(
  $requestedView = "TabularReportRedirect", 
  $defaultViewFix,

  AND(
	$requestedView <> NULL, 
    $userIsInSystemBuilders = TRUE,
    $requestedView = "Debug"
  ), 
  $requestedView, 

  AND(
	$userIsInEveryone <> TRUE, 
  	$userIsInExternalApprovers <> TRUE
  ),
  "External View", 

  AND(
	$requestedView = "Debug", 
    $userIsInAdministrators <> TRUE
  ), 
  $defaultViewFix,

  COALESCE(
    $requestedView, 
    $defaultViewFix,
    "Setup"
  )
)

FieldEntry.Formula:
LET $uniqueReference := [Mail Merge Purpose];

LET $recordIDs := 
DB_LOOKUP(
  ARRAY("RecordID"), 
  "Mail Merge Template", 
  "[LineNumber] = 1" & 
  " AND [Context EFormType] = " & DB_ENCODECONSTANT([EFormType]) & 
  " AND [Reference] = " & DB_ENCODECONSTANT($uniqueReference)
); 

LET $custom := 
ARRAY_SELECTMANY(
  $recordIDs, $(i, recordID) 
  (
	LET $files := 
	ARRAY_SELECT(
	  EBMS_GET_ATTACHMENTS("Mail Merge Template", $recordID, "Mailmerge"), $(i, item) 
	  HASH(
		"FileName", HASH_GET($item, "FileName"), 
		"RecordID", $recordID, 
		"Type", "Custom"
	  )
	); 

	RETURN $files
  )
); 

LET $result :=  
HASH(
  "Mail Merge RecordID", ARRAY_FIRST($recordIDs),
  "Custom Templates", $custom
);

RETURN $result

LET $columns :=  
ARRAY(
  "Type", 
  "Status", 
  "Round",
  "RecordID", 
  "Date Approved", 
  "Date Rejected", 
  "Rejected By Name", 
  "Approved By Name",
  "Assignee(s)"
);

LET $data := 
DB_LOOKUPALL(
  $columns,
  "Submission",
  "[LineNumber] FAST_EQUAL_TO 1",
  HASH("ancestor", ARRAY(EFormType, RecordID))
);

LET $data := ARRAY_SELECT($data, $(i, item) INDEXES_TO_KEYS($item, $columns));

LET $inDraft := ARRAY_WHERE($data, $(i, item) HASH_GET($item, "Status") = "Draft"); 
LET $submitted := ARRAY_WHERE($data, $(i, item) HASH_GET($item, "Status") = "Submitted"); 
LET $approved := ARRAY_WHERE($data, $(i, item) HASH_GET($item, "Status") = "Approved"); 


LET $result := 
HASH(
  "Count", 		 ARRAY_LENGTH($data),
  "Submitted", 	 ARRAY_LENGTH($submitted),
  "Draft", 		 ARRAY_LENGTH($inDraft),
  "Rounds", 	 GREATEST(ARRAY_SELECT($data, $(i,item) HASH_GET($item, "Round"))),
  "Raw", 		 $data, 
  "In Draft", 	 ARRAY_SELECT($inDraft, $(i, item) HASH_GET($item, "Type")),
  "In Progress", ARRAY_SELECT($submitted, $(i, item) HASH_GET($item, "Type")),
  "Approved", 	 
  (
	LET $approved := 
    ARRAY_DISTINCT(
	  ARRAY_CONCAT(
	    ARRAY_SELECT($approved, $(i, item) HASH_GET($item, "Type")),
	    ARRAY(
	  	  IF([Exemption Approved] = TRUE, "Exemption"),
	  	  IF([Date Plan Approved] <> NULL, "Procurement Plan"),
	  	  IF([Date Approved] <> NULL, "Procurement Initiation")
	    )
	  )
    );

    RETURN ARRAY_WHERE($approved, $(i, item) $item <> NULL)
  )
); 

RETURN $result

LET $existing := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Potential Vendor BusinessNumber],
		EACH [Potential Vendor Name],
		EACH [Potential Vendor RecordID]
	)
);

LET $newSet := 
ARRAY(
	[New Vendor BusinessNumber],
	[New Vendor Company Name],
	NULL()
);

LET $combined := 
ARRAY_CONCAT(
	$existing, 
	ARRAY($newSet)
);

LET $filtered := 
ARRAY_WHERE(
	$combined, $(i, item) 
	ARRAY_INDEX($item, 2) <> NULL()
);

LET $result := ARRAY_DISTINCT($filtered);

RETURN 
$result

