  "Type", 
  "Submission", 
  "[LineNumber] = 1 AND [RecordID] = " & $eventargs 
);
RETURN 
IF(
  $type = "Exemption", "Approval Events - Exemption", 
  $type = "Procurement Initiation", "Approval Events - Setup",
  $type = "Procurement Plan", "Approval Events - Plan"
)}

FieldEntry.Formula:
ARRAY_DISTINCT(
	ARRAY_WHERE(
		ARRAY_CONCAT(
			[Stages Complete], 
			ARRAY([Current Stage])
		), $(i,item)
		$item <> NULL()
	)
)

LET $new := HASH([Current Stage], NOW()); 
LET $existing := [Stage Completion Data];

RETURN 
HASH_MERGE(
  $existing, $new
)

LET $length:= ARRAY_LENGTH([Stage List]);

LET $index := ARRAY_INDEXOF([Stage List], "Evaluate");

RETURN
IF(
	$index = 0,
	[Stage List],

	LET $firstSlice := ARRAY_SLICE([Stage List], 1,$index-1);
	LET $secondSlice := ARRAY_SLICE([Stage List], $index+1, $length);

	RETURN
	ARRAY_CONCAT($firstSlice, $secondSlice)
	)

ARRAY_DISTINCT(
	ARRAY_WHERE(
		ARRAY_CONCAT(
			[Stages Complete], 
			ARRAY([Current Stage])
		), $(i,item)
		$item <> NULL()
	)
)

LET $new := HASH([Current Stage], NOW()); 
LET $existing := [Stage Completion Data];

RETURN 
HASH_MERGE(
  $existing, $new
)

LET $set := 
ARRAY_CONCAT(
  [Evaluation RecordIDs], 
  [TEMP - Evaluation RecordID]
);

RETURN 
ARRAY_SORT(
  ARRAY_DISTINCT(
    ARRAY_WHERE($set, $(i, item) $item <> NULL)
  )
)

