FieldEntry.Formula:
LET $set := 
ARRAY_WHERE(
  [Stage List], $(i,item)
  AND(
	$item <> "Evaluate", 
	$item <> "Evaluate 2"
  )
);

RETURN ARRAY_DISTINCT($set)

LET $bypass := 
DB_LOOKUP(
  "Demo Mode", 
  "Global Settings", 
  HASH("RecordID", 1)
);

LET $documentsRequired:=
ARRAY_TRANSPOSE(
ARRAY(
	EACH [Document Name],
	EACH [Stage Document Required],
	EACH [Document Attached to Reference]
));


LET $failedValidation:= 
ARRAY_WHERE(
  		$documentsRequired, $(i, item)
  		LET $documentName := ARRAY_INDEX($item, 1);
  		LET $stageRequired := ARRAY_INDEX($item, 2);
  		LET $documentUploaded := ARRAY_INDEX($item, 3);

  		RETURN 
		AND( 
			$stageRequired = [Navigation], //doc required in current stage
			$documentUploaded <> TRUE // required doc not attached
		)
);

RETURN
	IF(
  		AND(
		  	$bypass <> TRUE,
		  	ARRAY_LENGTH($failedValidation) > 0
		),
		HASH("errorIDs", "Documents Required", "messages", "Please upload the required documents."),
		NULL()
	)

LET $checklistRecordID := DB_LOOKUP( "Custom Checklist RecordID", "Procurement Process", "[RecordID] = " & DB_ENCODECONSTANT([Procurement Process RecordID]) );
LET $data := ARRAY_TRANSPOSE( DB_LOOKUPALL( ARRAY( "Initiate Item", "Plan Item", "Advertise Item", "Evaluate Item", "Award Item" ), "Custom Checklist", "[RecordID] = " & DB_ENCODECONSTANT($checklistRecordID) ) );
RETURN HASH( "Initiate", ARRAY_INDEX($data, 1), "Plan", ARRAY_INDEX($data, 2), "Advertise", ARRAY_INDEX($data, 3), "Evaluate", ARRAY_INDEX($data, 4), "Award", ARRAY_INDEX($data, 5) )

LET $stages := HASH_KEYS([Process List]);

LET $data := 
ARRAY_SELECT(
  $stages, $(s, stage)

  LET $processes := HASH_GET([Process List], $stage);
  LET $stageCheckList := HASH_GET([Checklist Data], $stage); 
  LET $checkListApplicable := 
  AND(
	ARRAY_LENGTH($stageCheckList) > 0, 
	$stageCheckList <> ARRAY(NULL()),
	ARRAY_INDEXOF($processes, $stage & " - Checklist")
  );

  RETURN 
  IF(
	$checkListApplicable = TRUE, 
	$processes, 
	ARRAY_WHERE($processes, $(p, process) $process <> $stage & " - Checklist")
  )
);

RETURN
INDEXES_TO_KEYS($data, $stages)

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $processColumns := 
ARRAY(
  //documents
  "Required Document", 
  "Document in Stage",

  //processes
  "Stage", 
  "Process", 
  "Process Enabled",

  //process settings
  "Go to Market", 
  "Setup Processes", 
  "Output", 
  "Output Procurement Category", 
  "Required Roles"
); 

LET $processData :=  
DB_LOOKUPALL(
  $processColumns,
  "Procurement Process", 
  "[RecordID] = " & DB_ENCODECONSTANT([Procurement Process RecordID])
);

LET $hashedProcessData := 
INDEXES_TO_KEYS(
  ARRAY_TRANSPOSE($processData), 
  $processColumns
);

LET $documents := 
ARRAY_WHERE(
  HASH_GET($hashedProcessData, "Required Document"), $(i,item) 
  $item <> NULL
);

LET $numberOfDocs := ARRAY_LENGTH($documents); 

LET $stageDocumentRequired := 
ARRAY_TAKE(
  HASH_GET($hashedProcessData, "Document in Stage"), 
  $numberOfDocs
);

LET $enabledProcesses := 
ARRAY_WHERE(
  ARRAY_TRANSPOSE(ARRAY(
    HASH_GET($hashedProcessData, "Stage"),
    HASH_GET($hashedProcessData, "Process"),
    HASH_GET($hashedProcessData, "Process Enabled")
  )), $(i, item) 
  AND(
    ARRAY_INDEX($item, 2) <> NULL, 
    ARRAY_INDEX($item, 3) == TRUE
  )
);

LET $processList := 
ARRAY_TO_HASH_GROUPED(
  $enabledProcesses, 
  $(i, item) ARRAY_INDEX($item, 1),
  $(i, item) ARRAY_INDEX($item, 2)
);

LET $stageList := 
(
  LET $list := HASH_KEYS($processList);

  LET $evaluate := 
  AND(
    ARRAY_INDEXOF(
      HASH_GET($processList, "Plan"), 
	  "Evaluation Planning"
    ) > 0,
	HASH_GET([Integrated Modules], "Owns Integrated Module - Evaluations") = TRUE
  ); 

  LET $list :=  
  ARRAY_CONCAT(
    "Setup", 
    ARRAY_WHERE($list, $(i, item) $item <> "Award"), 
    IF($evaluate = TRUE, "Evaluate"),
    "Award"
  ); 
  RETURN 
  ARRAY_DISTINCT(ARRAY_WHERE($list, $(i, item) $item <> NULL))
);

LET $processSettings := 
ARRAY_FIRST(ARRAY_TRANSPOSE(ARRAY(
  HASH_GET($hashedProcessData, "Go to Market"),
  HASH_GET($hashedProcessData, "Setup Processes"),
  HASH_GET($hashedProcessData, "Output"),
  HASH_GET($hashedProcessData, "Output Procurement Category"),
  HASH_GET($hashedProcessData, "Required Roles")
)));

LET $processSettingsHashed := 
INDEXES_TO_KEYS(
  $processSettings, 
  ARRAY(
    "Go to Market",
    "Setup Processes",
    "Output",
    "Output Procurement Category",
    "Required Roles"
  )
);

LET $requiredRoles := HASH_GET($processSettingsHashed, "Required Roles");

LET $role := 
(
  LET $unallocatedRequiredRoles := 
  ARRAY_WHERE(
    $requiredRoles, $(i, item) 
    EVALUATE("[" & $item & "]") = NULL
  );

  LET $existingData := 
  ARRAY_TRANSPOSE(
    ARRAY(
      EACH [Role], 
      EACH [Individual in Role]
    )
  );

  LET $populatedRoles :=  
  ARRAY_WHERE(
    $existingData, $(i, item) 
    AND(
  	  ARRAY_INDEX($item, 1) <> NULL,
  	  ARRAY_INDEX($item, 2) <> NULL
    )
  );

  LET $existingPopulatedRoles := 
  ARRAY_INDEX(ARRAY_TRANSPOSE($populatedRoles), 1);

  RETURN 
  ARRAY_DISTINCT(
    ARRAY_CONCAT(
      $existingPopulatedRoles, 
      $unallocatedRequiredRoles
    ) 
  )
);

LET $result := 
HASH(
  "Document Name", $documents, 
  "Stage Document Required", $stageDocumentRequired, 
  "Document Required", ARRAY_SELECT(RANGE(0, $numberOfDocs), $() TRUE), 
  "Process List", ARRAY($processList), 
  "Stage List", ARRAY($stageList), 
  "Stage Name", $stageList, 
  "Process Settings", ARRAY($processSettingsHashed), 
  "Required Roles", ARRAY($requiredRoles),
  "Role", $role, 
  "Output", ARRAY(HASH_GET($processSettingsHashed, "Output")), 
  "Output Procurement Category", ARRAY(HASH_GET($processSettingsHashed, "Output Procurement Category"))
);

RETURN 
IF( 
  $processData <> ARRAY(), 
    $result
)

FieldEntry.Formula:
LET $columns := ARRAY("Not Required", "Submission Type");

LET $data := 
DB_LOOKUPALL(
  $columns, 
  "Approval Workflow", 
  HASH("LineNumber", 1)
);

LET $hashed :=  
ARRAY_SELECT(
  $data, $(i, item) 
  INDEXES_TO_KEYS($item, $columns) 
);

RETURN 
INDEXES_TO_KEYS(
  $hashed, 
  ARRAY_SELECT($hashed, $(i, item) HASH_GET($item, "Submission Type"))
)

//Determines the list of steps to go through for the procurement
LET $setupProcessData := 
HASH_GET([Process Settings], "Setup Processes");

LET $fallbackList := 
ARRAY(
  "High Level Details",
  "Value",
  "Procurement Process", 
  "Roles"
); 

LET $processes := 
COALESCE(
  IF(
	ARRAY_LENGTH($setupProcessData) > 0, 
	HASH_KEYS($setupProcessData)
  ), 
  $fallbackList
);

LET $countOfStaticSteps := 4; 
LET $lockedProcesses := ARRAY_TAKE($processes, $countOfStaticSteps);
LET $flexibleProcesses := ARRAY_SKIP($processes, $countOfStaticSteps);

LET $awardTypeStepRequired := 
OR(
  [Output] = "Contract", 
  [Output] = "Subcontract",
  [Output] = "Panel Contract"
);

LET $exemptionRequired := [Exemption Required] = TRUE; 

LET $confirmationStepRequired := [Wizard Status] <> "Complete";

LET $checkListHasItems := HASH_GET([Checklist Data], "Initiate") <> NULL;

LET $filteredFlexibleProcesses := 
IF( 
  $checkListHasItems <> TRUE, 
  ARRAY_WHERE($flexibleProcesses, $(i, item) $item <> "Setup - Checklist"),
  $flexibleProcesses
); 

LET $combinedList := 
ARRAY_CONCAT(
  $lockedProcesses, 
  IF($awardTypeStepRequired, "Award Type"), 
  IF($exemptionRequired, "Exemption Approval"), 
  $filteredFlexibleProcesses,
  IF($confirmationStepRequired, "Confirmation")
);


LET $finalList := ARRAY_DISTINCT(ARRAY_WHERE($combinedList, $(i, item) $item <> NULL)); 

RETURN $finalList

FormulaDrivenRaiseLinkedEForm.FieldValuesFormula:
LET $columns := ARRAY( //High Level Details "Procurement Title", "Estimated Value", "Procurement Category", "Cost Centre", "Probity Advisor", "Department", "Scope of Work", "Category of Spend", "Subcategory of Spend", "Procurement Spend Type", "Contract Type", "Price Model",
//Roles - Usernames "Contract Manager", "Lawyer", "Probity Advisor", "Procurement Manager", "Procurement Officer", "Project Sponsor",
//Roles - Display Names "Lawyer Display Name", "Contract Manager Display Name", "Probity Advisor Display Name", "Procurement Manager Display Name", "Procurement Officer Display Name", "Project Sponsor Display Name",
//Roles - Repeater Logic "Role", "Role is a System User", "Username in Role", "Person in Role", "Individual in Role", "Individual Display Name in Role",
//Links "Project RecordID" );
LET $values := ARRAY_SELECT( $columns, $(i, col) CURRENT_EACHVALUE($col) );
LET $append := HASH( "Procurement Category", "RFT", "Source EFormType", EFormType, "Source RecordID", RecordID, "Market Approach", "Private" );
LET $result := HASH_MERGE( INDEXES_TO_KEYS($values, $columns), $append );
LET $preferredData := DB_LOOKUPALL( ARRAY( "RecordID", "Company Name", "BusinessNumber" ), "Respondent", "[Preferred] = TRUE AND [LineNumber] FAST_EQUAL_TO 1 AND [Procurement Number] = " & DB_ENCODECONSTANT([Procurement Number]) );
LET $preferredHash := INDEXES_TO_KEYS( ARRAY_TRANSPOSE($preferredData), ARRAY( "Potential Vendor RecordID", "Potential Vendor Name", "Potential Vendor BusinessNumber" ) );
RETURN HASH_MERGE( $result, $preferredHash )

FieldEntry.Formula:
LET $columns := ARRAY( "Procurement Process", "Low Value", "High Value", "Exemption Only", "Summary Statement", "Unlimited", "RecordID", //7 "Output", "Output Procurement Category", "Setup Approval Required", "Setup Checklist Enabled", "Procurement Value Check" );
LET $data := DB_LOOKUPALL( $columns, "Procurement Process", "[LineNumber] = 1 AND [Procurement Process] IS NOT EMPTY", HASH("sort", "[Low Value] ASC, [High Value] ASC, [Exemption Only] DESC") );
RETURN ARRAY_TO_HASH( $data, $(i,item) ARRAY_INDEX($item, ARRAY_INDEXOF($columns,"RecordID") ), $(i,item) INDEXES_TO_KEYS($item, $columns) )

LET $data := 
ARRAY_SELECT(
  HASH_KEYS([Wizard - Process Data]), $(i, item) 

  LET $name := HASH_GET(HASH_GET([Wizard - Process Data], $item), "Procurement Process");

  LET $highValue := HASH_GET(HASH_GET([Wizard - Process Data], $item), "High Value");
  LET $lowValue := HASH_GET(HASH_GET([Wizard - Process Data], $item), "Low Value");
  LET $exemptionOnly := HASH_GET(HASH_GET([Wizard - Process Data], $item), "Exemption Only");

  LET $valueCheckRequired := HASH_GET(HASH_GET([Wizard - Process Data], $item), "Procurement Value Check");  

  LET $exemptionRequired := 
  OR( 
    $exemptionOnly = TRUE,
  	AND(
	  $valueCheckRequired = TRUE, 
	  OR(
		[Estimated Value] > $highValue, 
        $lowValue > [Estimated Value]
	  )
	)
  );

  RETURN 
  ARRAY(
	$item, //RecordID
	$name,
	$highValue, 
	$exemptionRequired
  )
);

LET $result :=  
ARRAY_SORT(
  $data, $(item)
  ARRAY(
	ARRAY_INDEX($item, 4), //exemption required
	ARRAY_INDEX($item, 3), //high value
    ARRAY_INDEX($item, 2)  //name
  )
);

RETURN ARRAY_SELECT($result, $(i, item) ARRAY_INDEX($item, 1))

LET $stageToGoTo := $eventargs; 
LET $alreadyAccessed := [Processes Accessed];
LET $accessed := 
ARRAY_CONCAT(
  $alreadyAccessed, 
  ARRAY_FIRST(HASH_GET([Process List], $stageToGoTo))
); 
RETURN
IF( 
  $stageToGoTo = "Plan", 
    ARRAY_WHERE(ARRAY_DISTINCT($accessed), $(i, item) $item <> NULL),
  [Processes Accessed]
)

