FormulaCriterion.Formula:
//if this process is the last accessible process then only when trying to move forwards, otherwise always 


LET $targetProcessNumber := $eventargs; 
LET $currentProcessNumber := ARRAY_INDEXOF([This Stage Process List], ARRAY_FIRST([Visible Steps]));
LET $movingForward := $targetProcessNumber > $currentProcessNumber; 
LET $lastAccessibleProcess := ARRAY_LENGTH(ARRAY_WHERE(HASH_KEYS([Process Accessible]), $(p, process) HASH_GET([Process Accessible], $process) = TRUE));

RETURN
IF( 
  $currentProcessNumber == $lastAccessibleProcess, 
    $movingForward, 
  TRUE
)

