FormulaCriterion.Formula:
LET $canEditRecord := HASH_GET([Current User Permissions], "write");
LET $isCurrentStage := [Navigation] == [Current Stage];
LET $thisStep := ARRAY_FIRST([Visible Steps]);
LET $approvalInProgress := HASH_GET([Submission Data], "In Progress");
//get stored values of approvals
LET $storedApprovals :=
IF(
  	$canEditRecord = TRUE,
	DB_LOOKUP(
		ARRAY("Approval to Initiate Procurement Complete", "Plan Approval Complete"),
		"Procurement",
		"[LineNumber] FAST_EQUAL_TO 1 AND [RecordID] FAST_EQUAL_TO " & DB_ENCODECONSTANT([RecordID])
	),
  	NULL
);
//check if the current step is locked by approvals
LET $lockedByApproval :=
IF(
  	[Current Stage] = "Setup",
  	ARRAY_INDEXOF([This Stage Process List], "Approval") > ARRAY_INDEXOF([This Stage Process List], $thisStep),

  	[Current Stage] = "Plan",
  	ARRAY_INDEXOF([This Stage Process List], "Procurement Plan Approval") > ARRAY_INDEXOF([This Stage Process List], $thisStep),

  	FALSE
);

RETURN

AND(
  	$canEditRecord = TRUE,
  	$isCurrentStage = TRUE,
  	ARRAY([Approval to Initiate Procurement Complete], [Plan Approval Complete]) = $storedApprovals, //only save if the approval values match those stored in the database
  	ARRAY_LENGTH($approvalInProgress) = 0, //do not save if there are any in-progress approvals
  	OR(
	  	$lockedByApproval <> TRUE,
	  	AND([Current Stage] = "Setup", [Approval to Initiate Procurement Complete] <> TRUE),
	  	AND([Current Stage] = "Plan", [Plan Approval Complete] <> TRUE)
	)
)

FormulaDrivenRaiseLinkedEForm.FieldValuesFormula:
//LET $recordID := [RecordID];
LET $title := "Initial assesssment for " & [Procurement Title];
//LET $type := HASH_GET($eventargs, "Type");
//LET $viewMode := HASH_GET($eventargs, "View Mode");
LET $customRecordIDFilter := HASH_GET($eventargs, "Questionnaire RecordIDs Custom Filter");

LET $questionnaireRecordIDs :=
DB_LOOKUPALL(
  "RecordID",
  "Questionnaire", 
  COALESCE($customRecordIDFilter, "1 = 1") & " AND [LineNumber] FAST_EQUAL_TO 1" & 
  " AND [Status] <> 'Deleted' AND [Type] = "& DB_ENCODECONSTANT($type), 
  HASH("parent", ARRAY([EFormType], [RecordID]))
);

LET $questionnaireHeaderData :=
(
  LET $columns := 
  ARRAY(
  	"RecordID",
    "Status",
  	"Participant",
    "Participant Name",
  	"Assignee", 
  	"Evaluated Party", 
  	"Assessor Name", 
    "Assessor",
  	"Assessment Started",
  	"Assessment Complete",
  	"Completion - Allow Resubmit",
  	"Locked"
  );  

  LET $lookup := 
  DB_LOOKUPALL(
    $columns, 
    "Questionnaire", 
    "[LineNumber] FAST_EQUAL_TO 1 AND [Status] <> 'Deleted' AND [RecordID] IN " & DB_ENCODECONSTANT($questionnaireRecordIDs)
  );

  RETURN 
  ARRAY_TO_HASH(
  	$lookup, $(i,item) ARRAY_INDEX($item, 1), //RecordID
  	$(i,item) INDEXES_TO_KEYS($item, $columns)
  )
);

LET $questionnaireStatus :=
(
  LET $allStatus:= 
  ARRAY_DISTINCT(
    ARRAY_SELECT(
      $questionnaireRecordIDs, 
  	$(r, record) HASH_GET(HASH_GET($questionnaireHeaderData, $record), "Status")
  ));

  RETURN IF(
  	ARRAY_INDEXOF($allStatus, "Not Started") > 0, "Not Started",
    ARRAY_INDEXOF($allStatus, "In Progress") > 0, "In Progress",
    ARRAY_INDEXOF($allStatus, "Complete") > 0, "Complete",
    ARRAY_JOIN($allStatus, " ")
  )
);

RETURN 
HASH(
  "View Mode", 					$viewMode,
  "Type", 						ARRAY($type),
  "Title", 						ARRAY($title),
  "Questionnaire RecordIDs", 	ARRAY($questionnaireRecordIDs),
  "Questionnaire Header Data", 	ARRAY($questionnaireHeaderData), 
  "Questionnaire Status", 		ARRAY($questionnaireStatus),
  "Selected Questionnaire RecordIDs", $questionnaireRecordIDs
  //"Document List", 			ARRAY($documentList)
)

