FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $usertoReplace := HASH_GET($eventargs, "Previous Assignee");
LET $newUser := HASH_GET($eventargs, "New Assignee");

LET $assignees := EACH [Username in Role];

LET $reassignedRoles :=
ARRAY_SELECT(
	$assignees, $(i,item)
	IF($item = $usertoReplace, $newUser, $item)
);

LET $newData := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Role], 
		$reassignedRoles,  //1
		EACH [Custom Role],
		EACH [Person in Role], 
		EACH [Change of Role Status],
		EACH [Role is not a System User] //5
	)
);

RETURN 
INDEXES_TO_KEYS(
	ARRAY_TRANSPOSE(ARRAY_WHERE($newData,$(i,item) ARRAY_INDEX($item,2) <> NULL)),
	ARRAY(
		"Role",
		"Username in Role",
		"Custom Role",
		"Person in Role",
		"Change of Role Status",
		"Role is not a System User"
	)
)

FieldEntry.Formula:
LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Username in Role],
    EACH [Person in Role]
  )
); 

RETURN 
ARRAY_SELECT(
  $data, $(i, item) 
  LET $username := ARRAY_INDEX($item, 1); 
  LET $person := ARRAY_INDEX($item, 2); 
  RETURN COALESCE($username, $person)
)

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Role is not a System User],
    EACH [Individual in Role]
  )
); 

RETURN 
ARRAY_SELECT(
  $data, $(i, item) 
  LET $isSystemUser := NOT(ARRAY_INDEX($item, 1)); 
  LET $username := ARRAY_INDEX($item, 2);
  RETURN 
  IF(
	$isSystemUser <> TRUE, 
	  $username, 

	$username <> NULL, 
	  COALESCE(EBMS_GET_ROLE_DISPLAYNAME($username), $username)
  )
)

LET $columns :=  
ARRAY(
  "Type", 
  "Status", 
  "Round",
  "RecordID", 
  "Date Approved", 
  "Date Rejected", 
  "Rejected By Name", 
  "Approved By Name",
  "Assignee(s)"
);

LET $data := 
DB_LOOKUPALL(
	$columns,
    "Submission",
    "[LineNumber] FAST_EQUAL_TO 1",
    HASH("ancestor", ARRAY(EFormType, RecordID))
);

LET $data := ARRAY_SELECT($data, $(i, item) INDEXES_TO_KEYS($item, $columns));

LET $inDraft := ARRAY_WHERE($data, $(i, item) HASH_GET($item, "Status") = "Draft"); 
LET $submitted := ARRAY_WHERE($data, $(i, item) HASH_GET($item, "Status") = "Submitted"); 
LET $approved := ARRAY_WHERE($data, $(i, item) HASH_GET($item, "Status") = "Approved"); 


LET $result := 
HASH(
  "Count", 		 ARRAY_LENGTH($data),
  "Submitted", 	 ARRAY_LENGTH($submitted),
  "Draft", 		 ARRAY_LENGTH($inDraft),
  "Rounds", 	 GREATEST(ARRAY_SELECT($data, $(i,item) HASH_GET($item, "Round"))),
  "Raw", 		 $data, 
  "In Draft", 	 ARRAY_SELECT($inDraft, $(i, item) HASH_GET($item, "Type")),
  "In Progress", ARRAY_SELECT($submitted, $(i, item) HASH_GET($item, "Type")),
  "Approved", 	 
  (
	LET $approved := 
    ARRAY_DISTINCT(
	  ARRAY_CONCAT(
	    ARRAY_SELECT($approved, $(i, item) HASH_GET($item, "Type")),
	    ARRAY(
	  	  IF([Exemption Approved] = TRUE, "Exemption"),
	  	  IF([Date Plan Approved] <> NULL, "Procurement Plan"),
	  	  IF([Date Approved] <> NULL, "Procurement Initiation")
	    )
	  )
    );

    RETURN ARRAY_WHERE($approved, $(i, item) $item <> NULL)
  )
); 

RETURN $result

TriggerCustomEvent.EventNameTemplate:
{
LET $type:= 
DB_LOOKUP(
  "Type", 
  "Submission", 
  "[LineNumber] = 1 AND [RecordID] = " & $eventargs 
);
RETURN 
IF(
  $type = "Procurement Initiation", "Rejection Events - Setup",
  $type = "Procurement Plan", "Rejection Events - Plan"
)

}

TriggerCustomEvent.EventArgumentsFormula:
LET $user := "system";
LET $message := EBMS_GET_ROLE_DISPLAYNAME(EBMS_GET_CURRENT_USER()) & " unlocked read only stage '<b>" & [Navigation] & "</b>'. Provided reason: " & COALESCE([Unlocking Reason], "<Not provided>");

RETURN
HASH(
  "User", ROLE($user),
  "DateTime", NOW(),
  "Message", "<p>" & HTMLENCODE(EBMS_GET_ROLE_DISPLAYNAME($user)) & " " & $message & "</p>"
)

LET $startDate := COALESCE([Outgoing - Release Date], TODAY());
LET $endDate := COALESCE([Outgoing - Closing Date], DATEADD("day", 7, $startDate));
LET $decisionDate :=  [Outgoing - Decision Date];
//map estimated value to value range in GTM
LET $valueRangeID :=
IF(
  	[Estimated Value] <= 2500, 11,
  	[Estimated Value] <= 10000, 6,
  	[Estimated Value] <= 25000, 1,
  	[Estimated Value] <= 50000, 7,
  	[Estimated Value] <= 100000, 2,
  	[Estimated Value] <= 150000, 3,
  	[Estimated Value] <= 300000, 4,
  	[Estimated Value] <= 500000, 5,
  	[Estimated Value] <= 1000000, 8,
  	[Estimated Value] <= 3000000, 9,
  	[Estimated Value] > 3000000, 10,
  	11 //fall back to 0-2500 if there is an error
);

LET $newOpportunityModel := 
   HASH(

	  "externalReferenceNumber", [Outgoing - Tender Number],	 


	  "name", 			COALESCE([Outgoing - Name of Tender], ""),
	  "description",	COALESCE([Outgoing - Tender Description], ""),
	  "budgetValue",     FLOOR([Outgoing - Total Cost Projection]),

	 //how the suppliers are going to be evaluated
      "responseSelectionType", "Quick Select",

      // an RFx needs a nominated start date AND end date (UTC) in "Big O" format (eg. 2022-11-15T21:46:53.6853038Z)
	 //jsonencode changes datetime to utc
      "startDateUtc",    $startDate,
      "endDateUtc",      $endDate,
      "decisionDateUtc",  COALESCE($decisionDate, DATEADD("day", 7, $endDate)),

      // RFx is NOT complete
      "isComplete", FALSE,

      // RFx is NOT "same day close"
      "sameDayCloseEnabled", FALSE,
      "isSameDayClose", FALSE,

      // RFx is NOT a "demo rfx"
      "isDemo", FALSE,

     // Does the RFx require a quote to be provided, by default this is true
      "quotedAmountRequired", TRUE,

     // By default the value of the RFx is NOT disclosed to the supplier(s)
      "valueDisclosed", FALSE,

      // use default value range index
      "valueRangeId", $valueRangeID,

      // create an RFx that is at "step 2" in RFx Wizard
      "wizardStep", "1"
  );

LET $subCategories:=
ARRAY_SELECT(
	HASH_GET([Supplier Selection], "Categories"), $(p,pair)
		HASH(
			"parentCategoryId", HASH_GET($pair, "CategoryId"),
			"subCategoryId", 	HASH_GET($pair, "SubcategoryId")
		)
	);

RETURN 
HASH(
  "Request Source", 		ARRAY(EFormType, RecordID),
  "Request Type", 			"Create Rfx", 
  "opportunityModel",		$newOpportunityModel,
  "inviteSuppliers",		IF([Invite Preferred Vendors] = "Yes", TRUE, FALSE),
  "listType",				HASH_GET([Supplier Selection], "SupplierListType"),
  "selectedLists",			ARRAY_SELECTMANY( HASH_GET([Supplier Selection], "Lists"), $(i,item) HASH_GET($item, "Id")),
  "selectedSubcategories",	$subCategories,
  "selectedSuppliers",		ARRAY_SELECTMANY( HASH_GET([Supplier Selection], "Suppliers"), $(i,item) HASH_GET($item,"Id"))
  )

FieldEntry.Formula:
LET $set:= DATEDIFF_PARTS([Maximum End Date], [Start Date]);

LET $number:=
(	ARRAY_INDEX($set, 1) + //year
	ARRAY_INDEX($set, 2)/12  + //month
	ARRAY_INDEX($set, 3)/365 //day
);

RETURN
IF( 
  CEILING($number) > 20, 
  ARRAY(0,0,0),
  ARRAY_SELECT(
    RANGE(1, CEILING($number) ),
    $(i,item) 0
  )
)

ARRAY_DISTINCT(
	ARRAY_WHERE(
		ARRAY_CONCAT(
			[Stages Complete], 
			ARRAY($eventargs)
		), $(i,item)
		$item <> NULL()
	)
)

LET $new := HASH($eventargs, NOW()); 
LET $existing := [Stage Completion Data];

RETURN 
HASH_MERGE(
  $existing, $new
)

LET $currentQuestion := ARRAY_FIRST([Visible Steps]); 
LET $currentQuestionValidation := HASH_GET([Process Properties], $currentQuestion);

LET $hits := HASH_GET($currentQuestionValidation, "Validation"); 
LET $errorIDs := ARRAY_SELECTMANY($hits, $(i, item) HASH_GET($item, "Error ID"));
LET $messages := ARRAY_SELECTMANY($hits, $(i, item) HASH_GET($item, "Message"));

RETURN 
  HASH(
	"errorIDs", $errorIDs, 
	"messages", $messages,
	"stepFailed", IF(ARRAY_LENGTH($errorIDs) > 0, $currentQuestion),
	"stepPassed", IF(ARRAY_LENGTH($errorIDs) <= 0, $currentQuestion)
  )

LET $set := 
ARRAY_CONCAT(
  HASH_GET([Failed Validation], "failedSteps"), 
  ARRAY(HASH_GET([Validation Output], "stepFailed"))
);

LET $allRelevantProcesses := ARRAY_SELECTMANY(HASH_KEYS([Process List]), $(k, key) HASH_GET([Process List], $key)); 

LET $failedSteps :=  
ARRAY_WHERE(
  ARRAY_DISTINCT($set), $(i, item) 
  AND(
	$item <> HASH_GET([Validation Output], "stepPassed"),
	$item <> NULL,
	ARRAY_INDEXOF($allRelevantProcesses, $item) > 0
  )
);
LET $processList := 
HASH_MERGE(
  HASH("Setup", [Wizard Questions]),
  [Process List]
); 

LET $stages := HASH_KEYS($processList); 

LET $failedStages := 
ARRAY_WHERE(
  $stages, $(s, stage)

  LET $stepsInStage := HASH_GET($processList, $stage);

  LET $stepsThatFailedInThisStage := 
  ARRAY_WHERE( 
    $failedSteps, $(i, failedStep) 
	ARRAY_INDEXOF($stepsInStage, $failedStep) > 0
  );

  RETURN ARRAY_LENGTH($stepsThatFailedInThisStage) > 0
); 

RETURN
HASH(
  "failedSteps", $failedSteps,
  "failedStages", $failedStages
)

FormulaCriterion.Formula:
OR(   ARRAY_INDEXOF(HASH_GET([Process List], [Current Stage]), ARRAY_FIRST([Visible Steps])) > 0, 
  [Current Stage] = "Setup"
)

TriggerCustomEvent.TargetRecordsFormula:
/*eventargs keys = 
  "Void Exemption Approval", 
  "Void Plan Approval", 
  "Void Setup Approval",
  "Void All Approvals"
*/
LET $params := $eventargs;
//HASH("Void All Approvals", TRUE);  

LET $filterParts := 
ARRAY(
  IF( 
	OR(
	  HASH_GET($params, "Void Setup Approval") = TRUE, 
	  HASH_GET($params, "Void All Approvals") = TRUE
	), 
    "[Type] = 'Procurement Initiation'"
  ),
  IF( 	
	OR(
	  HASH_GET($params, "Void Plan Approval") = TRUE, 
	  HASH_GET($params, "Void All Approvals") = TRUE
	), 
    "[Type] = 'Procurement Plan'"

  ),
  IF( 	
	OR(
	  HASH_GET($params, "Void Exemption Approval") = TRUE, 
	  HASH_GET($params, "Void All Approvals") = TRUE
	), 
    "[Type] = 'Exemption'"
  ), 
  "1 = 1"
);

LET $filter := "(" & ARRAY_JOIN(ARRAY_WHERE($filterParts, $(i,item) $item <> NULL), " OR ") & ")";

LET $result := 
DB_LOOKUPALL(
  ARRAY("EFormType", "RecordID"), 
  "Submission",
  "[Status] <> 'Voided' AND " & $filter & " AND [LineNumber] = 1 AND [Significant Parent EFormType] = " & DB_ENCODECONSTANT([EFormType]) & " AND [Significant Parent RecordID] = " & DB_ENCODECONSTANT([RecordID]), 
);

RETURN $result

FormulaDrivenChangeFieldValues.FieldValuesFormula:
/*eventargs keys = 
  "Void Exemption Approval", 
  "Void Plan Approval", 
  "Void Setup Approval",
  "Void All Approvals"
*/
LET $params := $eventargs;

RETURN 
HASH_MERGE(
  IF( 
	OR(
	  HASH_GET($params, "Void Setup Approval") = TRUE, 
	  HASH_GET($params, "Void All Approvals") = TRUE
	), 
    HASH(
      "Approval to Initiate Procurement Complete", NULL, 
      "Date Approved", NULL
    )
  ),
  IF(
	OR(
	  HASH_GET($params, "Void Plan Approval") = TRUE, 
	  HASH_GET($params, "Void All Approvals") = TRUE
	), 
    HASH(
      "Plan Approval Complete", NULL, 
      "Date Plan Approved", NULL
    )
  ),
  IF(
	OR(
	  HASH_GET($params, "Void Exemption Approval") = TRUE, 
	  HASH_GET($params, "Void All Approvals") = TRUE
	), 
    HASH(
      "Exemption Approved", NULL
    )
  )
)

FieldEntry.Formula:
LET $data := HASH_GET([Wizard - Process Data], [Procurement Process RecordID]);
LET $upperThreshold := HASH_GET($data, "High Value"); 
LET $lowerThreshold := HASH_GET($data, "Low Value"); 
LET $exemptionOnly := HASH_GET($data, "Exemption Only"); 
LET $valueCheckRequired := HASH_GET($data, "Procurement Value Check");

LET $value := [Estimated Value];

LET $exemptionRequired := 
AND(
  $valueCheckRequired == TRUE, 
  OR(
    $value > $upperThreshold,
    $value < $lowerThreshold
  )
);

RETURN 
AND(
  [Estimated Value] <> NULL, 
  OR($exemptionRequired == TRUE, $exemptionOnly == TRUE)
)

//get the list of process properties relevant to this stage
LET $stepValidation :=
ARRAY_WHERE(
	[Process Properties], $(p,process)
	ARRAY_INDEXOF([This Stage Process List], ARRAY_FIRST(HASH_KEYS($process))) > 0
);

//get the array of completed steps
LET $completedSteps :=
ARRAY_SELECT(ARRAY_WHERE(
	$stepValidation, $(s,step)
	HASH_GET(ARRAY_INDEX($step,2), "Completion") == TRUE
), $(i,item) ARRAY_FIRST($item));

//get the list of steps which must be completed prior to the current step
LET $stepsToComplete :=
ARRAY_WHERE(
	[This Stage Process List], $(i,item)
	IF(
		//If the current step is approval, only the steps prior to approval need to be compeleted
		ARRAY_INDEXOF([Visible Steps], "Approval") > 0,
		$i < ARRAY_INDEXOF([This Stage Process List], "Approval"),
		//otherwise only exclude Confirmation
		$item <> "Confirmation"
	)
);

RETURN

//check that the array of completed steps contains all steps that must be completed to this point
ARRAY_WHERE($stepsToComplete, $(i,item) ARRAY_INDEXOF($completedSteps, $item) > 0) <> $stepsToComplete
