FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $columns := ARRAY("Department");

LET $data := DB_LOOKUP($columns, "Contract", HASH("RecordID", [Link to Contract]));

RETURN INDEXES_TO_KEYS($data, $columns)

FieldEntry.Formula:
LET $columnMap := 
HASH(
	"Item Description", "Item - Description",
	"Type", "Item Type",
	"Qty", "Item - Qty",	
	"Unit Price", "Item - Unit Price",
	"Tax Type", "Item - Tax ID",
);


LET $data := ARRAY_TRANSPOSE([Item Import]);

LET $structure := 
ARRAY_SELECT(
	$data, $(i, item) 
	LET $name := ARRAY_INDEX($item, 1); 
	LET $column := HASH_GET($columnMap, $name); 
	LET $data := ARRAY_SKIP($item, 1); 

	RETURN 
	ARRAY(
		$column, 
		$data
	)
);

RETURN 
ARRAY_TO_HASH(
	$structure, 
	$(i, item) 
	ARRAY_FIRST(ARRAY_TAKE($item, 1)),
	$(i, item) 
	ARRAY_FIRST(ARRAY_SKIP($item, 1))
)

FormulaDrivenRaiseLinkedEForm.FieldValuesFormula:
LET $cols:= ARRAY( "Parent", "Vendor", "Vendor Name", "Overall Budget", "Source EFormType", "Source RecordID", "Item Type", "Item - Qty", "Item - Unit Price", "Item - Tax ID", "Item - Description", "Posting Account", "Item - Posting Amount" );
LET $values:= ARRAY_SELECT( $cols, $(c,col) CURRENT_EACHVALUE($col) );
RETURN INDEXES_TO_KEYS( $values, $cols )

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $excludedColumns:= ARRAY( "Assignee", "DateClosed", "DateCreated", "DateDue", "DateLastUpdated", "Description", "EFormType", "LineNumber", "Originator", "Priority", "RecordID", "Sender", "WasRejected", "Purchase Order Number" );
LET $columns := ARRAY_WHERE(DB_GETCOLUMNS("Purchase Order Revision"), $(i, item) ARRAY_INDEXOF($excludedColumns, $item) = NULL()); LET $values := ARRAY_TRANSPOSE(DB_LOOKUPALL($columns, "Purchase Order Revision", HASH("RecordID", ARRAY_FIRST([Revision ID]))));
RETURN INDEXES_TO_KEYS($values, $columns)

