Binding.Expression:
IF(
	EBMS_GET_EFORMRECORD_PERMISSION(EFormType, RecordID, "write") <> TRUE,
	TRUE,
	AND(
		SUM(EACH [Quantity Received]) = 0,
		SUM(EACH [Received Amount]) = 0
	)
)
/* old formula 
IF(
	EBMS_GET_EFORMRECORD_PERMISSION(EFormType, RecordID, "write") <> TRUE,
		LET $sameTeam := DB_LOOKUP("RecordID", "User Properties", HASH("Team", [Assignee Team], "Username", EBMS_GET_CURRENT_USER())) <> NULL();

		RETURN
		IF($sameTeam,
			AND(
				SUM(EACH [Quantity Received]) = 0,
				SUM(EACH [Received Amount]) = 0
			),
			TRUE,
			FALSE
		),
	AND(
		SUM(EACH [Quantity Received]) = 0,
		SUM(EACH [Received Amount]) = 0
	)
)

TriggerCustomEvent.EventArgumentsFormula:
LET $targetType := "Purchase Order";
LET $mostRecentSubmission := DB_LOOKUP( ARRAY("RecordID", "Round"), "Submission", "[Type] = " & DB_ENCODECONSTANT($targetType), HASH("sort", "[Round] DESC", "ancestor", ARRAY(EFormType, RecordID)) );
RETURN HASH( "Submission Type", $targetType, "Previous Submission Round", COALESCE(ARRAY_LAST($mostRecentSubmission), 0), "Previous Submission RecordID", ARRAY_FIRST($mostRecentSubmission), "Parent Record", ARRAY([EFormType], [RecordID]), "Review Record", ARRAY([EFormType], [RecordID]), "Submitter Notes", NULL )

FormulaDrivenRaiseLinkedEForm.FieldValuesFormula:
LET $excludedColumns:= ARRAY( "Assignee", "DateClosed", "DateCreated", "DateDue", "DateLastUpdated", "Description", "EFormType", "LineNumber", "Originator", "Priority", "RecordID", "Sender", "WasRejected", "Purchase Order Number" );
LET $columns := ARRAY_WHERE(DB_GETCOLUMNS("Purchase Order Revision"), $(i, item) ARRAY_INDEXOF($excludedColumns, $item) = NULL()); LET $values := ARRAY_SELECT($columns, $(i, item) CURRENT_EACHVALUE($item));
RETURN INDEXES_TO_KEYS($values, $columns)

TriggerCustomEvent.TargetRecordsFormula:
LET $parentData := HASH_GET([Parent Structure], "Parent Data");
LET $parentContracts := ARRAY_SELECT(ARRAY_WHERE($parentData, $(i, item) ARRAY_FIRST($item) == "Contract"), $(e, eForm) ARRAY_TAKE($eForm, 2));

RETURN $parentContracts

FormulaCriterion.Formula:
LET $parentData := HASH_GET([Parent Structure], "Parent Data");
LET $parentContracts := ARRAY_SELECT(ARRAY_WHERE($parentData, $(i, item) ARRAY_FIRST($item) == "Contract"), $(e, eForm) ARRAY_TAKE($eForm, 2));

RETURN ARRAY_LENGTH($parentContracts) > 0

Navigate.UrlTemplate:
{
LET $recordID := 
DB_LOOKUP(
	"RecordID",
	"Submission",
	"[Status] IN ('Draft', 'Submitted') ",
	HASH("parent", ARRAY(EFormType, RecordID))
);

RETURN EBMS_GET_EFORMRECORD_URL("Submission", $recordID)
}
