Column.ValidationFormula:
LET $fails:=
ARRAY_SELECTMANY(
	ARRAY_INDEX($thisValue, 2), $(i,item)
	LET $mandatory := HASH_GET($item, "Mandatory");
	LET $respondentName := HASH_GET($item, "Evaluated Party");
	LET $answer := HASH_GET($item, "Answer");
	RETURN 
	IF(
	AND( 
		$mandatory = TRUE,
		$answer = NULL
		),
	//ARRAY($q, $question, $respondentName)
	$respondentName
	)
 );
RETURN
IF( 
	ARRAY_LENGTH($fails) >0 ,
	"Answer required for '"& ARRAY_JOIN( $fails, " AND " ),
	TRUE
)

Column.Formula:
LET $reopen:= ARRAY_SELECT( [Questionnaire RecordIDs], $(r, record) LET $data:= HASH_GET([Questionnaire Header Data], $record); RETURN AND( HASH_GET( $data, "Status") = "Complete", HASH_GET( $data, "Locked") <> TRUE, HASH_GET( $data, "Completion - Allow Resubmit") = TRUE )
);
RETURN IF( ARRAY_INDEXOF([User to Group Mappings], "System Builders") > 0, TRUE,
ARRAY_INDEXOF($reopen, TRUE) > 0, TRUE,
FALSE )

LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

LET $getValue := $(col) (
ARRAY_FIRST(ARRAY_DISTINCT(
  ARRAY_INDEX(
	ARRAY_TRANSPOSE($values),
	ARRAY_INDEXOF($columns, $col)
	)
  )
));

LET $cols:= 
ARRAY(
  "Result",
  "Result Description",
  "Scored",
  "Axes",
  "Show Category Results",
  "Max Score",
  "Total Score Percentage"
);

RETURN 
IF( 
  [View] = "Results",
  ARRAY_TO_HASH(
    $cols, $(i,item) $item,
     $(i,item) 
    CALL($getValue, $item)
  )
)

HASH_GET(
	HASH_GET([Attachment Data], "Images"), [Image Reference RecordID]#[LineNumber]
)
/*IF( 
  [Image Reference RecordID] <> NULL, 
  DB_LOOKUP(
	"Document Attached", 
	"Image Upload", 
	"[RecordID] = " & DB_ENCODECONSTANT([Image Reference RecordID])
  ) = TRUE
)

HASH_GET(
	HASH_GET([Attachment Data], "Documents"), [Document Reference RecordID]#[LineNumber]
)
/*IF( 
  [Document Reference RecordID] <> NULL, 
  DB_LOOKUP(
    "Document Attached", 
    "Document Upload", 
    "[RecordID] = " & DB_ENCODECONSTANT([Document Reference RecordID])
  ) = TRUE
)

LET $refs:=
ARRAY_WHERE(
  	EACH [Document Reference RecordID],
	$(i,item) $item <> NULL
  );


LET $filter := ARRAY_JOIN( ARRAY_SELECT($refs, $(i,item) "[RecordID] = "& DB_ENCODECONSTANT($item) ), " OR ");

LET $lookup := IF(
  	ARRAY_LENGTH($refs)>0,

	DB_LOOKUPALL(
	  "Document Attached", 
	  "Document Upload", 
	  $filter
	)
);

LET $doc := 
INDEXES_TO_KEYS( $lookup, $refs);

  //--image
  LET $irefs:=
ARRAY_WHERE(
  	EACH [Image Reference RecordID],
	$(i,item)  $item <> NULL
  );


LET $filter2 := ARRAY_JOIN( ARRAY_SELECT($irefs, $(i,item) "[RecordID] = "& DB_ENCODECONSTANT($item) ), " OR ");

LET $lookup2 := IF(
  	ARRAY_LENGTH($irefs)>0,

	DB_LOOKUPALL(
	   "Document Attached", 
	  "Image Upload", 
	  $filter2
	)
);

LET $image:= 
INDEXES_TO_KEYS( $lookup2, $irefs);

 RETURN

 HASH(
	"Documents", $doc,
	"Images", $image
 )

LET $title := [Title];
LET $len := LEN($title);
LET $limit := 70; 
LET $prefix := 
IF(
  [Landing View] = "Consensus", 
    "Consensus review: ",
  [Type] = "Evaluation", 
    "Evaluation for: "
);
LET $transformed := IF($len > $limit, LEFT($title, $limit) & "...", $title); 
RETURN $prefix & $transformed

LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName:= HASH(
  	"Insurance",	"Compliance"
  );

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF( $targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title:= 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID)
( EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

	  LET $parent := 
	  IF( 
		[Evaluation RecordID] >0, //[RecordID] > 0, 
		(
		  LET $record := ARRAY("Evaluation", [Evaluation RecordID]); //EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				  )

			  )
	  )); 

	  LET $gParent := 
	  IF( 
		 $parent <> NULL, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
			  )
		  )
	  )); 

	  LET $hash :=  
	  HASH_MERGE(
		  IF( $parent <> NULL, HASH("parent", 	$parent)),
		  IF( $gParent <> NULL, HASH("gparent", 	$gParent))
	  );

	  LET $parentStructure := 	//2nd instance
	  IF( 
		  ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
	  );

	  LET $parentData:= 
	  ARRAY_WHERE(
		ARRAY(
			$gParent, 
			$parent, 
			ARRAY_CONCAT($current, $currentDes, $currentAccess),
		), $(i,item) $item <> NULL
	   );


	  LET $parentChain:=
		ARRAY_SELECT(
			  $parentData, $(i, item)
			  ARRAY_TAKE($item, 2)
	   );


	  LET $parentLinks :=
	  ARRAY_SELECT(
		  $parentData, $(i, item) 
			  LET $eFormType 		:= ARRAY_FIRST($item);
			  LET $recordID 		:= ARRAY_INDEX($item, 2);
			  LET $descriptiveLabel := ARRAY_INDEX($item, 3);
			  LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
			  LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
			  LET $showEFormType := $eFormType <> "Vendor";

			  LET $labelColour := 
				  IF(
				   AND($eFormType = [EFormType], $recordID = [RecordID]),
					  "background: #ffffff; color: #5D5D5D;",
					 $hasReadAccess = TRUE,
					  "background: #ffffff; color: #4D87B7;",
					  "background: #ffffff; color: #5D5D5D;"
				  );


			  LET $labelColour := 
				  IF(
				   AND($eFormType = [EFormType], $recordID = [RecordID]),
					  "background: #ffffff; color: #5D5D5D;",
					 // $hasReadAccess = TRUE,
					 //"background: #ffffff; color: #4D87B7;",
					  "background: #ffffff; color: #4D87B7;"
				  );



			  LET $labeltext:= 
					  IF( $showEFormType == TRUE, " " &  COALESCE(HASH_GET($altFormName, $eFormType), $eFormType) ) &
					  IF(NOT(ISBLANK($descriptiveLabel)), " " & $descriptiveLabel);

			  LET $label :=  
				  "<span style=""margin-left: 5px;" & $labelColour & """>" &
				  HTMLENCODE($labeltext) & 
				  "</span>";

			  RETURN 
			  IF( 
				  $hasReadAccess == TRUE, 
				  "<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
				  $label 
			  )
	  );

	   LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 18px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";

	  LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

	  RETURN 
	  HASH(
		  "Parent Structure",	$parentStructure,
		  "Parent Data", 		$parentData,
		  "Parent Chain",		$parentChain,
		  "Breadcrumb",		$breadcrumb
		)
	 )
)

Column.DefaultValueFormula:
LET $columns := ARRAY( "Manual Response Creation", "Conflict of Interest Mode", "Clarification Log Mode", "Integrated Response Documents", "Mail Merge Export", "PP Criteria Locked Mode", "Shortlist Approval Required", "Final Decision Approval Required", "Individual Evaluation Completion Notification", "Discrepancy Threshold", "Consensus Scoring Method", "Evaluators Can Change Score During Consensus Scoring" );
LET $data := DB_LOOKUP( $columns, "Settings - Evaluation", "[RecordID] = 1" );
RETURN INDEXES_TO_KEYS($data, $columns)

Column.ValidationFormula:
LET $fails:=
ARRAY_SELECTMANY(
	HASH_KEYS([Question Data]), $(q, question)
	LET $valueArrays := HASH_GET([Question Data], $question);
  	RETURN
	ARRAY_SELECTMANY( 
		$valueArrays, $(i,item)
	  	LET $docMandatory := HASH_GET($item,"Document Upload Mandatory");

		LET $mandatory := 
	    IF(
		  [Type] = "Pre-Assessment", FALSE, 
		    [Type] = "Evaluation", FALSE, 

		  HASH_GET($item, "Mandatory")
	    );

		LET $displayID := HASH_GET($item, IF( OR( [Type]= "Evaluation", [Type] = "Pre-Assessment"), "Evaluated Party UUID", "RecordID") );
		LET $isSelected := [Current Question Number] = $q;
	  	LET $answerType:= HASH_GET($item, "Answer Type");
	  	LET $answerCol:= IF(
		  					$answerType = "Date", "Answer (Date)",
		  					$answerType = "Number", "Answer (Number)",
		  					$answerType = "Yes / No", "Answer (Bool)",
		  					$answerType = "Image", "Image Attached to Reference",
		  					"Answer"
		  				);
		LET $answer := IF( 
						$isSelected <> TRUE, HASH_GET($item, $answerCol),
		  				CURRENT_VALUE( $answerCol, ARRAY_INDEXOF(EACH [Display ID], T($displayID) ) )
						);
	  	LET $docAttached := IF( 
						$isSelected <> TRUE, HASH_GET($item, "Document Attached to Reference"),
		  				CURRENT_VALUE( "Document Attached to Reference", ARRAY_INDEXOF(EACH [Display ID], T($displayID) ) )
						);
	  	LET $notAnswered := IF( $answerType = "Image", $answer != TRUE, OR( $answer == NULL, $answer = "") );

	  	LET $docValidationFail:= IF( AND( $docMandatory = TRUE, $docAttached = FALSE),TRUE );

		RETURN 
		IF(
		OR(
		  AND( 
			$mandatory = TRUE,
			$notAnswered,
		  ),
		  $docValidationFail= TRUE
		  ),
			$q & $displayID & $answerCol & " - "& T($answer) & IF( $docValidationFail = TRUE, " - attachment required")
		)
	)	
 );
RETURN
IF( 
  ARRAY_LENGTH($fails) > 0,
  "All mandatory inputs have not been completed, please provide the mandatory information and then submit", //$fails,
  NULL
)

Binding.Expression:
LET $allQuestionData := [Question Data]; 
LET $answerTypes := ARRAY("Freetext", "Multi line freetext", "Yes / No", "Date", "Dropdown");


LET $styles := 
ARRAY_SELECT(
  ARRAY_CONCAT("Summary", HASH_KEYS($allQuestionData), "Submit"), $(k, key) 

  LET $questionID := ARRAY_INDEX(HASH_KEYS([Question Data]), ($k - 1)); 
  LET $questionInGroup := HASH_GET(ARRAY_FIRST([Current Question Data]), "Question Group");
  LET $data := ARRAY_FIRST(HASH_GET($allQuestionData, $key)); 

  LET $questionAnswered := CALL($isQuestionAnswered, $data); 

  LET $selected := 
  OR(
	//Summary selected
	AND([View] = "Submitting", $key = "Submit"), 
	//Summary selected
	AND([Current Question Number] = 0, $key = "Summary"), 
	//Exact question selected
	$key = HASH_GET(ARRAY_FIRST([Current Question Data]), "Question GUID"),
	//Question within group selected
	$key = $questionInGroup
  ); 

  LET $discrepancy := 
  (
	 GREATEST(ARRAY_SELECT(
	   HASH_KEYS([Aggregate Results]), $(c, criteria) 
       LET $questionList := HASH_GET([Aggregate Results], $criteria);
	   LET $questionData := HASH_GET($questionList, $questionID); 
	   LET $questionHasDiscrepancy := HASH_GET($questionData, "Discrepancy");
       RETURN IFERROR($questionHasDiscrepancy, FALSE)
	 ))
  );

  LET $answered := ARRAY_INDEXOF([Questions Answered], $key) > 0; 

  LET $css := 
  IF( 
	$selected = TRUE, 
	"[data-questionkey='" & $key & "'] > [data-state='selected'] {display: inline-block !important;}",

//	OR(
//    AND($key = "Submit", [Questionnaire Status] = "Complete"), 
//	  $answered = TRUE, 
//	), 
//	"[data-questionkey='" & $key & "'] > [data-state='answered'] {display: inline-block !important;}",

    $discrepancy,
    "[data-questionkey='" & $key & "'] > [data-state='discrepancy'] {display: inline-block !important;}",

	"[data-questionkey='" & $key & "'] > [data-state='notselected'] {display: inline-block !important;}"
  );

  RETURN $css

); 

RETURN "<style>" & ARRAY_JOIN($styles) & "</style>"

