//Categories and questions that don't have categories
LET $topLevelNavigation := HASH_GET([Question Structure], "Top Level");

//-------------
LET $questions:= 
ARRAY_SELECT(
  IF( 
	[Current Question Number] = 0, 
	ARRAY_CONCAT("Summary"),
	ARRAY_CONCAT("Summary", $topLevelNavigation)//, "Submit") not required on consensus view	
  ), $(i, questionKey)
  (
	LET $questionData := HASH_GET([Questions], $questionKey); 
    LET $detailedQuestionData := ARRAY_FIRST(HASH_GET([Question Data], $questionKey)); 
	LET $type := HASH_GET($questionData, "Question Type");

	LET $category := HASH_GET($questionData, "Category"); 
	LET $questionGroup := 
	IF( 
	  $type = "Category", 
	  HASH_GET($detailedQuestionData, "Question"), 
	  HASH_GET($detailedQuestionData, "Group Name")
	);
	LET $questionText := HASH_GET($questionData, "Question");

	LET $questionNumber := HASH_GET($questionData, "Question Number");
	LET $questionNumberInCategory := HASH_GET($questionData, "Question Number in Category");

 	LET $indexNumber := 
	IF( 
	  $type = "Category", 
	    ARRAY_INDEXOF(HASH_KEYS([Questions]), $questionKey),
	    //$questionNumber, 
	  $questionData <> NULL, 
	    ARRAY_INDEXOF(HASH_KEYS([Questions]), $questionKey),
	    //$questionNumber + 1, 
	  $questionKey = "Summary", 
	    0//, 
	  //$questionKey = "Submit", 
	  //  ARRAY_LENGTH([Question Data]) + 1
	); 

	LET $maxChar := 50; 
	LET $questionLength := LEN(HASH_GET($questionData, "Question")); 
	LET $wordArray := SPLIT(HASH_GET($questionData, "Question"), " "); 
	LET $questionText := 
	IF(
	  $questionLength > $maxChar, 
	  ARRAY_JOIN(ARRAY_TAKE($wordArray, 7), " ") & "...", 
	  HASH_GET($questionData, "Question")
	);

	LET $text := 
	IF(
	  $questionKey = "Summary", 	"Summary", 
	  //$questionKey = "Submit", 		"Submit", 
	  $type = "Info", 				$category, 
	  $type = "Category", 			IF($questionGroup <> NULL, $questionGroup, $category),
	  $questionText
	);

	LET $dataIDs := 
	"data-questionKey='" & $questionKey & "' " & 
	"data-clickid='" & $indexNumber & "' ";

	RETURN 	
	IF( 
	  OR(
		$questionKey = "Summary"//, 
	    //$questionKey = "Submit"
	  ), 
	  "<div class='core-digitalsubmissions-menu-heading'>" & $questionKey & "</div>" ,	  

	  $questionNumberInCategory = 1, 
	  "<div class='core-digitalsubmissions-menu-heading'>" & $category & "</div>" 
	) & 
    "<div " & $dataIDs & 
	  "style= ""display: table-row;  cursor: pointer; "" >" &

      "<div style='display: none;' data-state='selected' class='coreDigitalSubmissionsIconContainer'>" & REPLACESUBSTRING("currentColor", HASH_GET([Icon Map], "RightArrowCircle"), "#4682b4") & "</div>" & 
      "<div style='display: none;' data-state='answered' class='coreDigitalSubmissionsIconContainer'>" & REPLACESUBSTRING("currentColor", HASH_GET([Icon Map], "CheckCircle"), "#4ab446") & "</div>" & 
      "<div style='display: none;' data-state='notselected' class='coreDigitalSubmissionsIconContainer'>" & REPLACESUBSTRING("currentColor", HASH_GET([Icon Map], "Circle"), "#676767") & "</div>" & 
      "<div style='display: none;' data-state='discrepancy' class='coreDigitalSubmissionsIconContainer'>" & REPLACESUBSTRING("currentColor", COALESCE(HASH_GET([Icon Map], "Exclamation"), HASH_GET([Icon Map], "Circle")), "#FAA700") & "</div>" & 

      "<div class='coreConsensusReviewNavItemContainer'>" &
        $text & 
	  "</div>"&
    "</div>"
  )
);

RETURN 
"<div style='font-size: 10pt; background: #F8F8FF; padding: 15px; border-radius: 5px; display: block; height: calc(100% - 80px); position: relative; z-index: 2; width: 200px; overflow-y:auto; overflow-x: hidden'>" &
	"<div style='display: table; line-height:20px;'>" &
	"<div style='display: table-row; border: 2px solid red; font-weight: bold;'></div>" &
	ARRAY_JOIN($questions, "") & "</div>" &
"</div>"

ARRAY_SELECT(
  RANGE(1, CEILING(ARRAY_LENGTH([Respondents]) / 20)), $(i, item)
  HASH(
	//(1 - 1) * 20 + 1 = 0 * 20 + 1 = 1
	//(2 - 1) * 20 + 1 = 1 * 20 + 1 = 21
	"text", ($item - 1) * [Page Length] + 1 & " - " & LEAST(ARRAY_LENGTH([Respondents]), ($item * [Page Length])), 
	"value", $item
  )
)

LET $style := 
    "font-size: 16pt;
    height: 100%;
    overflow: auto;
    display: grid;
    line-height: 40px;";

LET $text := "Question "& [Current Question Number] & " of " & [Question Count] & " | Criteria: "& HASH_GET(HASH_GET([Questions], [Selected Question]), "Category");

RETURN
"<div style=""" & $style & """>" & 
REPLACESUBSTRING(NEWLINE(), $text, "<br>") & 
"</div>"

IF(
  [Document Attached to Reference]#[LineNumber] == TRUE, "View Documents", "Upload" 
  )

LET $answerData := [Answer Data]; 

LET $panelMembers := 
ARRAY_SELECT(
  $answerData, $(i, item) 
  HASH_GET($item, "Assessor")
);

LET $results := 
ARRAY_SELECT(
  $panelMembers, $(m, member)
  LET $data := ARRAY_INDEX($answerData, $m); 
  LET $answer := 
  IF( 
	HASH_GET($data, "Answer (Bool)") == TRUE, "Yes", 
	HASH_GET($data, "Answer (Bool)") == FALSE, "No",
	HASH_GET($data, "Answer")
  ); 	

  RETURN
  ARRAY(
    //assessor
	HASH_GET($data, "Assessor Name"), 

	//answer (XX.XX%)
	IF( 
	  $answer = NULL, 
	    "<Did not answer>" & " (0%)", 

	  $answer & " (" & NUMBERFORMAT(HASH_GET($data, "Answer Score Percentage Formatted"), "0.##") & "%)"
    ),
	//comment
	COALESCE(HASH_GET($data, "Comments"), "None provided"),

	//clickable
	IF(
      HASH_GET([Evaluation Settings], "Consensus Scoring Method") = "Average of Revised Individual Scores",
        HASH("value", "Edit", "clickid", HASH_GET($data, "Assessor")),
	    HASH("value", ""),
	)
  )
);

LET $averageScore := 
NUMBERFORMAT(
  AVERAGE(
    (
      LET $scores := ARRAY_SELECT(
        $answerData, $(i, data)
        LET $answer := 
        IF( 
	      HASH_GET($data, "Answer (Bool)") == TRUE, "Yes", 
	      HASH_GET($data, "Answer (Bool)") == FALSE, "No",
	      HASH_GET($data, "Answer")
        ); 
	    RETURN 
	    IF( 
	  	  $answer <> NULL,
	  	    HASH_GET($data, "Answer Score Percentage Formatted"), 
		  NULL
		  //0
	    )
      );

	  RETURN $scores
	)
  ), 
  "0.##"
);



LET $averageRow := 
ARRAY(
  "Average", 
  $averageScore & "%", 
  "", 
  ""
); 


LET $columns := 
  ARRAY(
    HASH(
  	  "heading", "Panel Member", 
  	  "width", 160
    ),
    HASH(
  	  "heading", "Response (Score)", 
  	  "width", 220
    ),
    HASH(
  	  "heading", "Comment", 
  	  "width", 260
    ),
    HASH(
  	  "heading", "", 
  	  "width", 40
    )
  ); 

LET $table :=  
HASH(
  "columns", $columns,
  "rows", ARRAY_CONCAT($results, ARRAY($averageRow))
);

RETURN $table

