FieldEntry.Formula:
LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

LET $match :=  
ARRAY_FIRST(ARRAY_WHERE(
  $values, $(v, value) 
  AND(
    ARRAY_INDEX($value, ARRAY_INDEXOF($columns, "RecordID")) = [Selected Questionnaire RecordID], 
	ARRAY_INDEX($value, ARRAY_INDEXOF($columns, "LineNumber")) = [Selected Questionnaire LineNumber], 
  )
));

LET $maxScore := ARRAY_INDEX($match, ARRAY_INDEXOF($columns, "Answer Max Score")); 

LET $optionScoreMap := 
INDEXES_TO_KEYS(
  ARRAY_INDEX($match, ARRAY_INDEXOF($columns, "Option Scores")),
  ARRAY_INDEX($match, ARRAY_INDEXOF($columns, "Options"))
); 

LET $columnsToUpdate := 
HASH(
  "Questionnaire LineNumber", [Selected Questionnaire LineNumber],
  "Questionnaire RecordID",   [Selected Questionnaire RecordID],
  "Answer",  			[Answer],
  "Answer (Bool)",  	[Answer (Bool)],
  "Answer (Number)",  	[Answer (Number)],
  "Answer (Date)", 		[Answer (Date)],
  "Comments", 			[Comments]  ,
  "Change Reason", 		"Consensus Evaluation", 
  "Score", 				HASH_GET($optionScoreMap, [Answer]),
  "Answer Score Percentage Formatted", ROUND(HASH_GET($optionScoreMap, [Answer]) / $maxScore * 100, 2),
  "Answer Score",		HASH_GET($optionScoreMap, [Answer])
);

LET $updated :=  
ARRAY_SELECT(
  $match, $(i, item) 
  LET $column := ARRAY_INDEX($columns, $i);
  LET $updateThis := ARRAY_INDEXOF(HASH_KEYS($columnsToUpdate), $column) > 0;
  LET $result := 
  IF( 
    $updateThis, 
	  HASH_GET($columnsToUpdate, $column),
	$item
  );
  RETURN $result
);

LET $notChanged := 
ARRAY_WHERE(
  $values, $(v, value) 
  NOT(AND(
    ARRAY_INDEX($value, ARRAY_INDEXOF($columns, "RecordID")) = [Selected Questionnaire RecordID], 
	ARRAY_INDEX($value, ARRAY_INDEXOF($columns, "LineNumber")) = [Selected Questionnaire LineNumber], 
  ))
);

RETURN 
INDEXES_TO_KEYS(
  ARRAY(
    $columns, 
	ARRAY_SORT(
	  ARRAY_CONCAT(
		ARRAY($updated), $notChanged), 
	    $(item) 
	  ARRAY(
	    ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Evaluated Party")),
        ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "LineNumber")),
	    ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Evaluated Party UUID"))
	  )
	)
  ), 
  ARRAY(
    "columns", 
	"values"
  )
)

Binding.Expression:
AND(
  [Consensus Editing Answer] = TRUE//,
  //HASH_GET([Question Display Data], "Answer Type") = "Dropdown"
)

//START TEXT
LET $partyID := [Display ID];

LET $evaluation := 
OR(
  [Type] = "Evaluation", 
  [Type] = "Pre-Assessment"
);

LET $pre := IF($evaluation = TRUE, "RESPONDENT", "QUESTIONNAIRE");

LET $post := 
IF(
  $evaluation = TRUE,
    HASH_GET([Question Display Data], "Evaluated Party")  & " (" & $partyID & ")",

  [Display ID] & " " & HASH_GET([Question Display Data], "Description")
);

LET $headingText :=  
IF( 
  AND(
	$evaluation <> TRUE,
	ARRAY_LENGTH([Selected Questionnaire RecordIDs]) <= 1
  ), 
    NULL,

  $pre & " - " & $post & 
  IF( 
	[Selected Assessor] <> NULL, 
	" - Update Answer for " & EBMS_GET_ROLE_DISPLAYNAME([Selected Assessor])
  )
);

LET $headingHTML :="<div style='padding-top: 1px; float: left; font-size: 9pt; font-weight: bold;'>" & $headingText & "</div>";
//END TEXT

//START ICON LOGIC
LET $criteria := HASH_GET(HASH_GET([Questions], [Selected Question]), "Category");

LET $data := 
HASH_GET(
  HASH_GET([Aggregate Results], $criteria), 
  [Selected Question]
);

LET $thisPartyScore := HASH_GET(HASH_GET($data, $partyID), "Average");
LET $highscore := HASH_GET($data, "Group Highest Average") = $thisPartyScore; 
LET $discrepancy := HASH_GET(HASH_GET($data, $partyID), "Discrepancy"); 

LET $iconHTML := 
"<div style='padding-left: 2px; float: left; text-align: right;'>" & 
  IF(AND($thisPartyScore > 0, $highscore = TRUE), HASH_GET([Icons], "Award")) &
  IF($discrepancy = TRUE, HASH_GET([Icons], "Discrepancy")) & 
"</div>";
//END ICON LOGIC

RETURN "<div style='padding-top: 5px'>" & $headingHTML & $iconHTML & "</div>"

ARRAY_SELECT(
  RANGE(1, CEILING(ARRAY_LENGTH([Respondents]) / 20)), $(i, item)
  HASH(
	//(1 - 1) * 20 + 1 = 0 * 20 + 1 = 1
	//(2 - 1) * 20 + 1 = 1 * 20 + 1 = 21
	"text", ($item - 1) * [Page Length] + 1 & " - " & LEAST(ARRAY_LENGTH([Respondents]), ($item * [Page Length])), 
	"value", $item
  )
)

LET $fails:=
ARRAY_SELECTMANY(
	HASH_KEYS([Question Data]), $(q, question)
	LET $valueArrays := HASH_GET([Question Data], $question);
  	RETURN
	ARRAY_SELECTMANY( 
		$valueArrays, $(i,item)
	  	LET $docMandatory := HASH_GET( $item,"Document Upload Mandatory");
		LET $mandatory := HASH_GET($item, "Mandatory");
		LET $displayID := HASH_GET($item, IF( OR( [Type]= "Evaluation", [Type] = "Pre-Assessment"), "Evaluated Party UUID", "RecordID") );
		LET $isSelected := [Current Question Number] = $q;
	  	LET $answerType:= HASH_GET($item, "Answer Type");
	  	LET $answerCol:= IF(
		  					$answerType = "Date", "Answer (Date)",
		  					$answerType = "Number", "Answer (Number)",
		  					$answerType = "Yes / No", "Answer (Bool)",
		  					$answerType = "Image", "Image Attached to Reference",
		  					"Answer"
		  				);
		LET $answer := IF( 
						$isSelected <> TRUE, HASH_GET($item, $answerCol),
		  				CURRENT_VALUE( $answerCol, ARRAY_INDEXOF(EACH [Display ID], T($displayID) ) )
						);
	  	LET $docAttached := IF( 
						$isSelected <> TRUE, HASH_GET($item, "Document Attached to Reference"),
		  				CURRENT_VALUE( "Document Attached to Reference", ARRAY_INDEXOF(EACH [Display ID], T($displayID) ) )
						);
	  	LET $notAnswered := IF( $answerType = "Image", $answer != TRUE, OR( $answer == NULL, $answer = "") );

	  	LET $docValidationFail:= IF( AND( $docMandatory = TRUE, $docAttached = FALSE),TRUE );

		RETURN 
		IF(
		OR(
		  AND( 
			$mandatory = TRUE,
			$notAnswered,
			),
		  $docValidationFail= TRUE
		  ),
			$q & $displayID & $answerCol & " - "& T($answer) & IF( $docValidationFail = TRUE, " - attachment required")
		)
	)	
 );
RETURN
IF( 
	ARRAY_LENGTH($fails) >0 ,
	TRUE,
	FALSE
)

LET $questionText := 
ARRAY_SELECT(
  HASH_KEYS([Questions]), $(i ,key)
  HASH_GET(HASH_GET([Questions], $key), "Question")
);

RETURN
COALESCE(
  ARRAY_SELECT(
	RANGE(0, [Question Count] + 1), $(i,item)
	 HASH(
		"value", $item,
	  	"text", 
	       IF(
			 $item = 0, "Summary", 
			 "Question "& $item & " - " & ARRAY_INDEX($questionText, $item)
	       )
		)	
	),
  ARRAY()
)

