TriggerCustomEvent.TargetRecordsFormula:
LET $completed:= 
ARRAY_WHERE(
  	HASH_KEYS([Questionnaire Header Data]), $(i,item)
  	LET $data:= HASH_GET([Questionnaire Header Data], $item);
  	RETURN HASH_GET($data, "Status") = "Complete"
);
RETURN
