  HASH_KEYS([Question Data]), $(i, key) 
  LET $data := ARRAY_FIRST(HASH_GET([Question Data], $key));
  RETURN 
  OR(
    HASH_GET($data, "Question Type") = "Category",
    HASH_GET($data, "Question Type") = "Unscored",
    HASH_GET($data, "Question Type") = "Scored",
    HASH_GET($data, "Question Type") = "Info"
  )
));

