RETURN 
IF( 
  $percent > 0, 
  "<div style='
     width: 230px; 
     background-color: #f1f1f1'
  >
     <div style='
       background-color: #4682b4; 
       color: white; 
       padding: 2px 0px 2px 0px; 
       font-size: 10px;
       width:" & $percent & "%'
       > " & $percent & "%
     </div>
   </div>"
)

