Binding.Expression:
LET $line := [LineNumber]; 

LET $documentData := [Document List]#$line;

LET $displayKey := NULL;//HASH_GET([Question Display Data]#$line, "Evaluated Party") & " ("&  [Display ID]#$line  & ")";
LET $key := REPLACESUBSTRING("VPR", [Display ID]#$line, "");

LET $documentData := HASH_GET($documentData, $key);

LET $documentsHTML := 
IF( 
  ARRAY_LENGTH($documentData) > 0, 
  ARRAY_SELECT(
    $documentData, $(d, document)
    (
      LET $source := HASH_GET($document, "Source");
	  LET $fileName := HASH_GET($document, "FileName");
	  LET $recordID := HASH_GET($document, "Respondent Source RecordID");

	  LET $url := 
	  COALESCE(
		HASH_GET($document, "URL"), 
		"files/eformrecords/Respondent/" & HASH_GET($document, "Respondent Source RecordID") & "/Response/" & URLENCODE($fileName)
	  );

	  //Required for VP download
	  LET $sourcingID := HASH_GET($document, "Sourcing ID");
	  LET $responseID := T(HASH_GET($document, "Response ID"));
	  LET $documentID := HASH_GET($document, "Document ID");

      RETURN 
      IF( 
        $source = "VendorPanel", 
		"<div class='coreDocumentListDownloadLink' data-clickid='" & $source &"/|\"& $sourcingID &"/|\"& $responseID &"/|\"& $documentID & "'>" & 
		  HTMLENCODE($fileName) & 
		"</div>",

		"<div class='coreDocumentListDownloadLink'>" & 
		  "<a style='text-decoration: none;' href='" & $url & "'>" & HTMLENCODE($fileName) & "</a>" &
		"</div>"
	  )
    )
  ),
  "<div class='coreDocumentListNoDocuments'>" & HTMLENCODE("None") & "</div>" 
);

LET $documentOutput := 
"<div class='coreDocumentList'>" & 
  "<div class='coreDocumentListHeading'>Documents</div>" & 
  //"<div style='margin-top: 5px; font-weight: bold;'>" & $displayKey & "</div>" & 
  IF(
    ARRAY_LENGTH($documentData) > 0, 
    "<div class='coreDocumentListDownloadAllLink' data-clickid='DownloadAll/|\" & $sourcingID & "/|\" & $responseID & "'>[Download All]</div>",
  ) &
  ARRAY_JOIN($documentsHTML) & 
"</div>";

RETURN 
"<div class='coreDocumentListContainer'>" & 
  ARRAY_JOIN(
  ARRAY_SELECT(
    $documentOutput, $(i, documentSet) 
    $documentSet
  )
) & 
"</div>"

