FieldEntry.Formula:
LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

LET $match :=  
ARRAY_FIRST(ARRAY_WHERE(
  $values, $(v, value) 
  AND(
    ARRAY_INDEX($value, ARRAY_INDEXOF($columns, "RecordID")) = HASH_GET([Question Display Data], "RecordID"), 
	ARRAY_INDEX($value, ARRAY_INDEXOF($columns, "LineNumber")) = HASH_GET([Question Display Data], "LineNumber"), 
  )
));

LET $maxScore := ARRAY_INDEX($match, ARRAY_INDEXOF($columns, "Answer Max Score")); 

LET $optionScoreMap := 
INDEXES_TO_KEYS(
  ARRAY_INDEX($match, ARRAY_INDEXOF($columns, "Option Scores")),
  ARRAY_INDEX($match, ARRAY_INDEXOF($columns, "Options"))
); 

LET $columnsToUpdate := 
HASH(
  "Questionnaire LineNumber", HASH_GET([Question Display Data], "LineNumber"),
  "Questionnaire RecordID",   HASH_GET([Question Display Data], "RecordID"),
  "Answer",  			[Answer],
  "Answer (Bool)",  	[Answer (Bool)],
  "Answer (Number)",  	[Answer (Number)],
  "Answer (Date)", 		[Answer (Date)],
  "Comments", 			[Comments]  ,
  "Change Reason", 		"Consensus Evaluation", 
  "Score", 				HASH_GET($optionScoreMap, [Answer]),
  "Answer Score Percentage Formatted", ROUND(HASH_GET($optionScoreMap, [Answer]) / $maxScore * 100, 2),
  "Answer Score",		HASH_GET($optionScoreMap, [Answer])
);

LET $updated :=  
ARRAY_SELECT(
  $match, $(i, item) 
  LET $column := ARRAY_INDEX($columns, $i);
  LET $updateThis := ARRAY_INDEXOF(HASH_KEYS($columnsToUpdate), $column) > 0;
  LET $result := 
  IF( 
    $updateThis, 
	  HASH_GET($columnsToUpdate, $column),
	$item
  );
  RETURN $result
);

LET $notChanged := 
ARRAY_WHERE(
  $values, $(v, value) 
  NOT(AND(
    ARRAY_INDEX($value, ARRAY_INDEXOF($columns, "RecordID")) = HASH_GET([Question Display Data], "RecordID"), 
	ARRAY_INDEX($value, ARRAY_INDEXOF($columns, "LineNumber")) = HASH_GET([Question Display Data], "LineNumber"), 
  ))
);

RETURN 
INDEXES_TO_KEYS(
  ARRAY(
    $columns, 
	ARRAY_SORT(
	  ARRAY_CONCAT(
		ARRAY($updated), 
		$notChanged
	  ), 
	  $(item) 
	  ARRAY(
		ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Evaluated Party")), 
		ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "LineNumber")), 
		ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Evaluated Party UUID"))
	  )
	)
  ), 
  ARRAY(
    "columns", 
	"values"
  )
)

