AND(
  [Page Number] <> NULL,
  [View] = "Questions",
  [Selected Evaluators] = NULL, 
  OR( 
	HASH_GET([Question Display Data], "Answer Type") = "Multi line freetext",
	HASH_GET([Question Display Data], "Comments Applicable") = TRUE
	)
  )
)

LET $style := 
    "font-size: 12pt;
	font-weight: bold;
    height: 100%;
    overflow: auto;
    display: grid;
    margin: auto;
    align-items: center;
    text-align: left; ";
LET $question:= HASH_GET(HASH_GET([Questions], [Selected Question]), "Question");
RETURN
"<div style=""" & $style & """>" & 
REPLACESUBSTRING(NEWLINE(), $question, "<br>") & 
"</div>"

