  )

LET $pre := IF(OR( [Type] = "Evaluation", [Type] = "Pre-Assessment"), "RESPONDENT", "QUESTIONNAIRE");
LET $post := IF( OR([Type] = "Evaluation", [Type] = "Pre-Assessment"), HASH_GET([Question Display Data], "Evaluated Party") & " ("& [Display ID] & ")",
[Display ID] &" "& HASH_GET([Question Display Data], "Description") );
RETURN IF( AND( [Type] <> "Evaluation", [Type] <> "Pre-Assessment", ARRAY_LENGTH([Selected Questionnaire RecordIDs]) <= 1 ), NULL,$pre & " - " & $post )

ARRAY_SELECT(
  RANGE(1, CEILING(ARRAY_LENGTH([Respondents]) / 20)), $(i, item)
  HASH(
	//(1 - 1) * 20 + 1 = 0 * 20 + 1 = 1
	//(2 - 1) * 20 + 1 = 1 * 20 + 1 = 21
	"text", ($item - 1) * [Page Length] + 1 & " - " & LEAST(ARRAY_LENGTH([Respondents]), ($item * [Page Length])), 
	"value", $item
  )
)

LET $questionText := 
